import React, { useEffect, useState, useContext } from 'react';
import {
  fetchMovies,
  getAllBookings,
  deleteMovie,
  getAdminStats
} from '../../services/api';
import { useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';

function AdminDashboard() {
  const [movies, setMovies] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [stats, setStats] = useState({ users: 0, movies: 0, bookings: 0 });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);

  useEffect(() => {
    if (!user || user.role !== 'admin') {
      navigate('/');
      return;
    }

    Promise.all([
      fetchMovies(),
      getAllBookings(),
      getAdminStats()
    ])
      .then(([movieRes, bookingRes, statsRes]) => {
        setMovies(movieRes?.data || []);
        setBookings(bookingRes?.data?.bookings || []);
        setStats(statsRes?.data || { users: 0, movies: 0, bookings: 0 });
        setLoading(false);
      })
      .catch(err => {
        console.error('❌ Admin data fetch error:', err.response?.data || err.message);
        setError('Failed to load admin data. Please check your server or token.');
        setLoading(false);
      });
  }, [navigate, user]);

  const handleDelete = async (movieId) => {
    const confirm = window.confirm('Are you sure you want to delete this movie?');
    if (!confirm) return;

    try {
      await deleteMovie(movieId);
      setMovies(prev => prev.filter(m => m._id !== movieId));
    } catch (err) {
      console.error('❌ Movie deletion error:', err.response?.data || err.message);
      alert('Failed to delete movie');
    }
  };

  if (loading) return <div className="p-6 text-center text-lg animate-pulse text-gray-600">Loading admin dashboard...</div>;
  if (error) return <div className="p-6 text-center text-red-500">{error}</div>;

  return (
    <div className="max-w-7xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-8 text-red-500">🛠️ Admin Dashboard</h1>

      {/* 📊 Admin Stats */}
      <section className="mb-12">
        <h2 className="text-2xl font-semibold mb-4">Overview</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-center">
          <div className="bg-blue-100 p-4 rounded shadow">
            <h3 className="text-xl font-bold">{stats.users}</h3>
            <p className="text-gray-700">Users</p>
          </div>
          <div className="bg-green-100 p-4 rounded shadow">
            <h3 className="text-xl font-bold">{stats.movies}</h3>
            <p className="text-gray-700">Movies</p>
          </div>
          <div className="bg-yellow-100 p-4 rounded shadow">
            <h3 className="text-xl font-bold">{stats.bookings}</h3>
            <p className="text-gray-700">Bookings</p>
          </div>
        </div>
      </section>

      {/* 🎬 Movie Management */}
      <section className="mb-12">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-2xl font-semibold">Movies</h2>
          <Link to="/admin/add-movie">
            <button className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
              ➕ Add Movie
            </button>
          </Link>
        </div>

        {movies.length === 0 ? (
          <p className="text-gray-500 italic">No movies found.</p>
        ) : (
          <div className="grid gap-4">
            {movies.map(movie => (
              <div key={movie._id} className="flex flex-col md:flex-row md:items-center justify-between p-4 border rounded shadow-sm bg-gray-50">
                <div className="mb-2 md:mb-0">
                  <h3 className="text-lg font-semibold text-gray-800">{movie.title || 'Untitled'}</h3>
                  <p className="text-sm text-gray-600">
                    {movie.genre || 'Genre'} • {movie.rating || 'N/A'}/10 • {movie.language || 'Language'}
                  </p>
                  <p className="text-xs text-gray-500">
                    Released: {movie.releaseDate ? new Date(movie.releaseDate).toLocaleDateString() : 'Unknown'}
                  </p>
                </div>
                <div className="flex gap-2">
                  <Link to={`/admin/edit-movie/${movie._id}`}>
                    <button className="px-3 py-1 bg-yellow-500 text-white rounded hover:bg-yellow-600">
                      Edit
                    </button>
                  </Link>
                  <button
                    onClick={() => handleDelete(movie._id)}
                    className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700"
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* 🎟️ Booking Overview */}
      <section>
        <h2 className="text-2xl font-semibold mb-4">Bookings</h2>

        {bookings.length === 0 ? (
          <p className="text-gray-500 italic">No bookings found.</p>
        ) : (
          <div className="grid gap-4">
            {bookings.map(booking => (
              <div key={booking._id} className="p-4 border rounded shadow-sm bg-gray-50">
                <h3 className="font-semibold text-gray-800">{booking.movie?.title || 'Unknown Movie'}</h3>
                <p className="text-sm text-gray-600">User: {booking.user?.email || 'N/A'}</p>
                <p className="text-sm text-gray-600">
                  Showtime: {booking.showtimeDate ? new Date(booking.showtimeDate).toLocaleString() : 'N/A'}
                </p>
                <p className="text-sm text-gray-600">Seats: {booking.seats?.join(', ') || 'N/A'}</p>
                <p className={`text-sm font-semibold ${booking.status === 'confirmed' ? 'text-green-600' : 'text-red-600'}`}>
                  Status: {booking.status}
                </p>
                {booking.theater && (
                  <p className="text-sm text-gray-600">
                    Theater: {booking.theater.name} ({booking.theater.location})
                  </p>
                )}
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
}

export default AdminDashboard;
