import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function MovieDetails() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/movies/${id}`)
      .then(res => {
        setMovie(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error("Error fetching movie:", err);
        setLoading(false);
      });
  }, [id]);

  if (loading) {
    return <div className="p-6 text-center text-lg animate-pulse text-gray-600">Loading movie details...</div>;
  }

  if (!movie) {
    return <div className="p-6 text-center text-red-600">Movie not found.</div>;
  }

  return (
    <div className="max-w-5xl mx-auto p-4">
      {/* Poster & Title */}
      <div className="flex flex-col md:flex-row gap-6 mb-6">
        <img
          src={movie.posterUrl}
          alt={movie.title}
          className="w-full md:w-1/3 rounded-lg shadow-lg object-cover"
        />
        <div>
          <h1 className="text-3xl font-bold mb-2">{movie.title}</h1>
          <p className="text-gray-700 mb-4">{movie.description}</p>
          <p className="text-sm text-gray-500">Genre: {movie.genre}</p>
          <p className="text-sm text-yellow-500">‚≠ê Rating: {movie.rating}</p>
          <p className="text-sm text-gray-500">Duration: {movie.duration}</p>
          {movie.trailerUrl && (
            <a
              href={movie.trailerUrl}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-block mt-4 px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
            >
              Watch Trailer
            </a>
          )}
        </div>
      </div>

      {/* Showtimes */}
      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-2">Showtimes</h2>
        <div className="flex flex-wrap gap-3">
          {movie.showtimes.map((time, index) => (
            <div key={index} className="px-4 py-2 bg-gray-800 text-white rounded">
              {new Date(time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
            </div>
          ))}
        </div>
      </div>

      {/* Cast */}
      <div>
        <h2 className="text-xl font-semibold mb-2">Cast</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {movie.cast.map((actor, index) => (
            <div key={index} className="text-center">
              {actor.photoUrl ? (
                <img
                  src={actor.photoUrl}
                  alt={actor.name}
                  className="w-24 h-24 object-cover rounded-full mx-auto mb-2"
                />
              ) : (
                <div className="w-24 h-24 bg-gray-300 rounded-full mx-auto mb-2 flex items-center justify-center text-sm text-gray-600">
                  No Photo
                </div>
              )}
              <p className="font-medium">{actor.name}</p>
              <p className="text-sm text-gray-500">{actor.role}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default MovieDetails;
