import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

function Navbar() {
  const navigate = useNavigate();
  const { user, setUser, setIsAuthenticated } = useContext(AuthContext);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('role');
    setUser(null);
    setIsAuthenticated(false);
    navigate('/login');
  };

  const role = localStorage.getItem('role') || user?.role || '';

  return (
    <nav className="bg-black text-white px-6 py-4 shadow-md sticky top-0 z-50">
      <div className="max-w-6xl mx-auto flex flex-col md:flex-row justify-between items-center gap-4">
        {/* Logo */}
        <Link to="/" className="text-2xl font-bold text-red-500 hover:text-red-400 transition">
          ShowSnap
        </Link>

        {/* Navigation Links */}
        <div className="flex flex-wrap gap-3 items-center text-sm">
          {user ? (
            <>
              <span className="text-gray-400">Hi, {user.name?.split(' ')[0] || 'User'}</span>

              <Link
                to="/bookings"
                className="bg-gray-800 px-4 py-2 rounded hover:bg-gray-700 transition"
              >
                My Bookings
              </Link>

              {role === 'admin' && (
                <Link
                  to="/admin"
                  className="bg-yellow-600 px-4 py-2 rounded hover:bg-yellow-700 transition"
                >
                  Admin Dashboard
                </Link>
              )}

              <button
                onClick={handleLogout}
                className="bg-gray-700 px-4 py-2 rounded hover:bg-gray-600 transition"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link
                to="/login"
                className="bg-red-600 px-4 py-2 rounded hover:bg-red-700 transition"
              >
                Login
              </Link>
              <Link
                to="/signup"
                className="bg-blue-600 px-4 py-2 rounded hover:bg-blue-700 transition"
              >
                Sign Up
              </Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
