const handleRazorpayPayment = async (payload) => {
  const res = await fetch('http://localhost:5000/api/payments/create-order', {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ amount: payload.amount })
  });

  const { orderId } = await res.json();

  const options = {
    key: 'rzp_test_RIn3HX6YPDL3Nf',
    amount: payload.amount,
    currency: 'INR',
    name: 'ShowSnap',
    description: 'Movie Ticket Booking',
    order_id: orderId,
    handler: async function (response) {
      // ✅ Only after payment is successful
      alert('✅ Payment successful!');

      const token = localStorage.getItem('token');
      const user = JSON.parse(localStorage.getItem('user'));

      // Save booking to DB
      const bookingRes = await bookSeats(payload, token);
      const bookingId = bookingRes?.data?._id;

      // Send confirmation email
      await fetch('http://localhost:5000/api/email/booking-confirmation', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          booking: payload,
          userEmail: user.email
        })
      });

      // Redirect to summary page
      if (bookingId) {
        window.location.href = `/my-bookings/${bookingId}`;
      }
    },
    prefill: {
      name: 'Harshal',
      email: 'harshal@example.com',
      contact: '9999999999'
    },
    theme: {
      color: '#F37254'
    }
  };

  const rzp = new window.Razorpay(options);
  rzp.open();
};
