import axios from 'axios';

// Create Axios instance
const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE_URL || 'http://localhost:5000/api',
  timeout: 10000,
});

// Request interceptor for token injection
API.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Optional: Response interceptor for global error logging
API.interceptors.response.use(
  response => response,
  error => {
    console.error('API Error:', error.response?.data || error.message);
    return Promise.reject(error);
  }
);

//
// ðŸŽ¬ Movie APIs
//
export const fetchMovies = () => API.get('/movies');
export const getMovieById = (id) => API.get(`/movies/${id}`);
export const createMovie = (movieData) => API.post('/admin/movies', movieData);
export const updateMovie = (id, data) => API.put(`/admin/movies/${id}`, data);
export const deleteMovie = (movieId) => API.delete(`/admin/movies/${movieId}`);
export const fetchCastByMovie = (movieId) => API.get(`/movies/${movieId}/cast`);

//
// ðŸ‘¤ User Auth APIs
//
export const loginUser = (data) => API.post('/users/login', data);
export const signupUser = (userData) => API.post('/users/register', userData);

//
// ðŸŽŸï¸ Booking APIs
//
export const bookSeats = (data) => API.post('/bookings', data);
export const getUserBookings = (userId) => API.get(`/bookings/user/${userId}`);
export const cancelBooking = (bookingId) => API.put(`/bookings/cancel/${bookingId}`);

//
// ðŸ› ï¸ Admin APIs
//
export const getAllBookings = () => API.get('/admin/bookings');
export const getAdminStats = () => API.get('/admin/dashboard');
export const getAllUsers = () => API.get('/admin/users');

//
// ðŸ¢ Theater APIs
//
export const fetchTheaters = () => API.get('/theaters');

export default API;
