import React, { useEffect, useState, useMemo } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import handleRazorpayPayment from '../components/RazorpayCheckout';

function BookMovie() {
  const { id } = useParams();
  const navigate = useNavigate();

  // üé¨ Movie & Booking States
  const [movie, setMovie] = useState(null);
  const [selectedTheaterIndex, setSelectedTheaterIndex] = useState('');
  const [selectedShowtime, setSelectedShowtime] = useState('');
  const [seatCount, setSeatCount] = useState(1);
  const [seatMap, setSeatMap] = useState([]);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const user = JSON.parse(localStorage.getItem('user'));
  const token = localStorage.getItem('token');

  const rows = useMemo(() => ['A', 'B', 'C', 'D'], []);
  const cols = useMemo(() => [1, 2, 3, 4, 5, 6], []);
  const pricePerSeat = 250;
  const totalPrice = selectedSeats.length * pricePerSeat;

  // üîπ Helper: Format YouTube URL
  const getYouTubeEmbedUrl = (url) => {
    try {
      const videoId = url.includes('youtu.be')
        ? url.split('youtu.be/')[1]
        : url.split('v=')[1].split('&')[0];
      return `https://www.youtube.com/embed/${videoId}`;
    } catch {
      return '';
    }
  };

  // üîπ Helper: Format showtime
  const formatShowtime = (time) => {
    if (!time) return 'Not selected';
    const parsed = new Date(time);
    return isNaN(parsed.getTime()) ? 'Invalid Date' : parsed.toLocaleString('en-IN');
  };

  // üîπ Fetch Movie
  useEffect(() => {
    const fetchMovie = async () => {
      if (!id) return setError('Invalid movie ID');
      try {
        const res = await fetch(`http://localhost:5000/api/movies/${id}`);
        const data = await res.json();
        setMovie(data);
      } catch (err) {
        console.error('‚ùå Error fetching movie:', err);
        setError('Failed to load movie data');
      }
    };
    fetchMovie();
  }, [id]);

  // üîπ Fetch Seat Map for selected theater & showtime
  useEffect(() => {
    const fetchSeats = async () => {
      if (!selectedTheaterIndex || !selectedShowtime || !movie) return;

      try {
        const theater = movie.theaters[selectedTheaterIndex];
        const res = await fetch(
          `http://localhost:5000/api/bookings/seats?movieId=${id}&theater=${encodeURIComponent(
            theater.name
          )}&showtime=${encodeURIComponent(selectedShowtime)}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        const bookedSeats = await res.json(); // Array of booked seat IDs

        const newSeatMap = rows.flatMap((row) =>
          cols.map((col) => {
            const seatId = `${row}${col}`;
            return {
              id: seatId,
              status: bookedSeats.includes(seatId) ? 'booked' : 'available',
            };
          })
        );

        setSeatMap(newSeatMap);
        setSelectedSeats([]);
      } catch (err) {
        console.error('‚ùå Error fetching seat map:', err);
        setError('Failed to load seat availability');
      }
    };

    fetchSeats();
  }, [selectedTheaterIndex, selectedShowtime, movie, id, token, rows, cols]);

  // üîπ Toggle Seat Selection
  const toggleSeat = (seatId) => {
    const seat = seatMap.find((s) => s.id === seatId);
    if (seat?.status === 'booked') return;

    if (selectedSeats.includes(seatId)) {
      setSelectedSeats(selectedSeats.filter((s) => s !== seatId));
    } else {
      if (selectedSeats.length < seatCount) {
        setSelectedSeats([...selectedSeats, seatId]);
      } else {
        alert(`You can only select ${seatCount} seat(s).`);
      }
    }
  };

  // üîπ Prepare Booking Payload
  const handleBooking = async () => {
    if (!token || !user) {
      navigate('/login');
      return null;
    }

    if (!movie || selectedTheaterIndex === '') {
      setError('Please select a theater');
      return null;
    }

    if (!selectedShowtime || selectedSeats.length !== seatCount) {
      setError('Please select showtime and exact number of seats');
      return null;
    }

    const theater = movie.theaters[selectedTheaterIndex];

    return {
      movieId: id,
      userId: user._id,
      showtimeDate: selectedShowtime,
      seats: selectedSeats,
      theater: {
        name: theater.name,
        location: theater.location,
      },
      amount: totalPrice * 100,
    };
  };

  // üîπ Initiate Payment
  const initiatePayment = async () => {
    const payload = await handleBooking();
    if (!payload) return;

    try {
      await handleRazorpayPayment(payload);
      setSuccess('üéâ Booking successful!');
      setSelectedSeats([]);
      setSelectedShowtime('');
      setSelectedTheaterIndex('');
      setSeatMap([]);
    } catch (err) {
      console.error('‚ùå Payment failed:', err);
      setError('Payment failed. Please try again.');
    }
  };

  // üîπ Render Loader
  if (!movie) {
    return <div className="p-6 text-center text-lg animate-pulse text-gray-600">Loading movie...</div>;
  }

  return (
    <div className="p-6 max-w-4xl mx-auto bg-white text-gray-900 rounded-lg shadow-lg">
      {/* Movie Title & Poster */}
      <h2 className="text-4xl font-bold mb-6 text-center text-red-600">{movie.title}</h2>
      <div className="flex justify-center mb-6">
        <img
          src={movie.posterUrl}
          alt={movie.title}
          className="w-64 h-[360px] object-cover rounded-xl shadow-lg"
        />
      </div>

      {/* Trailer */}
      {movie.trailerUrl && (
        <div className="mb-6">
          <h3 className="text-xl font-semibold mb-2 text-center">Watch Trailer</h3>
          <div className="relative w-full" style={{ paddingBottom: '56.25%', height: 0 }}>
            <iframe
              className="absolute top-0 left-0 w-full h-full rounded-lg shadow-md"
              src={getYouTubeEmbedUrl(movie.trailerUrl)}
              title="YouTube trailer"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            />
          </div>
        </div>
      )}

      {/* Movie Info */}
      <div className="text-center mb-6">
        <p className="text-gray-700">{movie.description}</p>
        <p><strong>Genres:</strong> {movie.genre || 'N/A'}</p>
        <p><strong>Rating:</strong> {movie.rating || 'N/A'}/10</p>
      </div>

      {/* Cast */}
      {movie.cast?.length > 0 && (
        <div className="mb-6">
          <h3 className="text-xl font-semibold mb-4 text-center">Cast & Crew</h3>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-4 justify-center">
            {movie.cast.map((actor, index) => (
              <div
                key={index}
                className="flex items-center gap-3 bg-gray-50 p-3 rounded shadow-sm hover:shadow-md"
              >
                <img
                  src={actor.photoUrl}
                  alt={actor.name}
                  className="w-12 h-12 rounded-full object-cover border"
                />
                <div>
                  <p className="font-semibold text-gray-800">{actor.name}</p>
                  <p className="text-sm text-gray-600">{actor.role}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Booking Controls */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        {/* Theater */}
        <div>
          <label className="block font-semibold mb-1">Select Theater</label>
          <select
            value={selectedTheaterIndex}
            onChange={(e) => {
              setSelectedTheaterIndex(Number(e.target.value));
              setSelectedShowtime('');
            }}
            className="border p-2 rounded w-full bg-white"
          >
            <option value="">-- Choose a theater --</option>
            {movie.theaters?.map((t, idx) => (
              <option key={idx} value={idx}>{t.name} ({t.location})</option>
            ))}
          </select>
        </div>

        {/* Showtime */}
        <div>
          <label className="block font-semibold mb-1">Select Showtime</label>
          <select
            value={selectedShowtime}
            onChange={(e) => setSelectedShowtime(e.target.value)}
            className="border p-2 rounded w-full bg-white"
          >
            <option value="">-- Choose a showtime --</option>
            {selectedTheaterIndex !== '' &&
              movie.theaters[selectedTheaterIndex]?.showtimes?.map((time, idx) => (
                <option key={idx} value={time}>{formatShowtime(time)}</option>
              ))
            }
          </select>
        </div>

        {/* Seat Count */}
        <div>
          <label className="block font-semibold mb-1">Number of Seats</label>
          <select
            value={seatCount}
            onChange={(e) => {
              setSeatCount(Number(e.target.value));
              setSelectedSeats([]);
            }}
            className="border p-2 rounded w-full bg-white"
          >
            {[1, 2, 3, 4, 5].map((count) => (
              <option key={count} value={count}>{count}</option>
            ))}
          </select>
        </div>
      </div>

      {/* Seat Selection Grid */}
      {seatMap.length > 0 && (
        <div className="space-y-3 mt-4 mb-6">
          {rows.map((row) => (
            <div key={row} className="flex gap-2 justify-center">
              {cols.map((col) => {
                const seatId = `${row}${col}`;
                const seat = seatMap.find((s) => s.id === seatId);
                const isSelected = selectedSeats.includes(seatId);
                const isBooked = seat?.status === 'booked';

                return (
                  <button
                    key={seatId}
                    disabled={isBooked}
                    onClick={() => toggleSeat(seatId)}
                    className={`px-3 py-2 rounded border font-semibold text-sm transition duration-200
                      ${isBooked ? 'bg-gray-500 text-white cursor-not-allowed' :
                        isSelected ? 'bg-green-600 text-white' : 'bg-gray-200 hover:bg-gray-300'}`}
                  >
                    {seatId}
                  </button>
                );
              })}
            </div>
          ))}
        </div>
      )}

      {/* Booking Summary */}
      <div className="mt-6 p-4 bg-gray-100 rounded shadow-sm text-center">
        <h3 className="text-lg font-semibold mb-2">Booking Summary</h3>
        <p><strong>Movie:</strong> {movie.title}</p>
                <p><strong>Theater:</strong> {movie.theaters[selectedTheaterIndex]?.name || 'Not selected'}</p>
        <p><strong>Showtime:</strong> {formatShowtime(selectedShowtime)}</p>
        <p><strong>Seats:</strong> {selectedSeats.length > 0 ? selectedSeats.join(', ') : 'None selected'}</p>
        <p><strong>User:</strong> {user?.name} ({user?.email})</p>
        <p className="text-indigo-600 font-bold mt-2">Total Price: ‚Çπ{totalPrice}</p>
      </div>

      {/* Payment / Book Now Button */}
      <button
        onClick={initiatePayment}
        disabled={selectedSeats.length !== seatCount}
        className={`mt-6 px-6 py-3 rounded font-semibold text-lg w-full transition-colors
          ${selectedSeats.length !== seatCount
            ? 'bg-gray-400 cursor-not-allowed'
            : 'bg-indigo-600 text-white hover:bg-indigo-700'
          }`}
      >
        Pay ‚Çπ{totalPrice} & Book Now
      </button>

      {/* Error & Success Messages */}
      {error && <p className="text-red-500 mt-2 text-center">{error}</p>}
      {success && <p className="text-green-600 mt-2 text-center">{success}</p>}
    </div>
  );
}

export default BookMovie;
