import React, { useEffect, useState, useMemo } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Home() {
  const navigate = useNavigate();
  const [movies, setMovies] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedGenre, setSelectedGenre] = useState("");
  const [minRating, setMinRating] = useState(0);

  // 🔹 Fetch movies (always ensure array)
  useEffect(() => {
    const fetchMovies = async () => {
      try {
        const { data } = await axios.get("/api/movies");
        // handle both array or {movies:[]} style responses
        setMovies(Array.isArray(data) ? data : data.movies || []);
      } catch (err) {
        console.error(err);
        setMovies([]); // fallback to empty array
      }
    };
    fetchMovies();
  }, []);

  // 🔹 Movies currently released (Now Showing)
  const visibleMovies = useMemo(() => {
    const now = new Date();
    return movies.filter(
      (movie) =>
        (!movie.releaseDate || new Date(movie.releaseDate) <= now) &&
        movie.title?.toLowerCase().includes(searchTerm.toLowerCase()) &&
        (selectedGenre
          ? movie.genre?.toLowerCase().includes(selectedGenre.toLowerCase())
          : true) &&
        movie.rating >= minRating
    );
  }, [movies, searchTerm, selectedGenre, minRating]);

  // 🔹 Upcoming movies (Coming Soon)
  const upcomingMovies = useMemo(() => {
    return movies.filter((m) => new Date(m.releaseDate) > new Date());
  }, [movies]);

  return (
    <div className="home-page">
      {/* 🔍 Search Bar */}
      <div className="filters mb-6 flex gap-4">
        <input
          type="text"
          placeholder="Search movies..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="border p-2 rounded"
        />

        <select
          value={selectedGenre}
          onChange={(e) => setSelectedGenre(e.target.value)}
          className="border p-2 rounded"
        >
          <option value="">All Genres</option>
          <option value="action">Action</option>
          <option value="drama">Drama</option>
          <option value="thriller">Thriller</option>
          <option value="romance">Romance</option>
          <option value="sci-fi">Sci-Fi</option>
          <option value="comedy">Comedy</option>
        </select>

        <select
          value={minRating}
          onChange={(e) => setMinRating(Number(e.target.value))}
          className="border p-2 rounded"
        >
          <option value={0}>All Ratings</option>
          <option value={5}>5+ ⭐</option>
          <option value={7}>7+ ⭐</option>
          <option value={8}>8+ ⭐</option>
        </select>
      </div>

      {/* 🎥 Now Showing */}
      <h2 className="text-2xl font-bold mb-4">Now Showing</h2>
      <div className="movies-grid grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
        {visibleMovies.map((movie) => (
          <div key={movie._id} className="movie-card">
            <img
              src={movie.posterUrl}
              alt={movie.title}
              onClick={() => navigate(`/movies/${movie._id}`)}
              className="cursor-pointer rounded-lg w-full h-64 object-cover"
            />
            <h3
              onClick={() => navigate(`/movies/${movie._id}`)}
              className="cursor-pointer mt-2 text-center font-semibold"
            >
              {movie.title}
            </h3>
          </div>
        ))}
      </div>

      {/* 🎥 Coming Soon */}
      <h2 className="text-2xl font-bold mt-10 mb-4">Coming Soon</h2>
      <div className="movies-grid grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
        {upcomingMovies.map((movie) => (
          <div key={movie._id} className="movie-card">
            <img
              src={movie.posterUrl}
              alt={movie.title}
              onClick={() => navigate(`/movies/${movie._id}`)}
              className="cursor-pointer rounded-lg w-full h-64 object-cover"
            />
            <h3
              onClick={() => navigate(`/movies/${movie._id}`)}
              className="cursor-pointer mt-2 text-center font-semibold"
            >
              {movie.title}
            </h3>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
