import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000/api',
  timeout: 10000, // â±ï¸ Prevent hanging requests
});

// ðŸŽ¬ Movies
export const fetchMovies = () => API.get('/movies');
export const getMovieById = (id) => API.get(`/movies/${id}`);

// ðŸ‘¤ Auth
export const loginUser = (data) => API.post('/users/login', data);
export const signupUser = (data) => API.post('/users/signup', data);

// ðŸŽŸï¸ Bookings
export const bookSeats = async (data, token) => {
  try {
    return await API.post('/bookings', data, {
      headers: { Authorization: `Bearer ${token}` },
    });
  } catch (err) {
    console.error('API booking error:', err.response?.data || err.message);
    throw err;
  }
};

export const getUserBookings = (userId, token) =>
  API.get(`/bookings/user/${userId}`, {
    headers: { Authorization: `Bearer ${token}` },
  });

export const cancelBooking = (bookingId, token) =>
  API.put(`/bookings/cancel/${bookingId}`, null, {
    headers: { Authorization: `Bearer ${token}` },
  });

// ðŸ›¡ï¸ Admin
export const getAllBookings = (token) =>
  API.get('/admin/bookings', {
    headers: { Authorization: `Bearer ${token}` },
  });

export const deleteMovie = (movieId, token) =>
  API.delete(`/admin/movies/${movieId}`, {
    headers: { Authorization: `Bearer ${token}` },
  });

export const createMovie = (movieData, token) =>
  API.post('/admin/movies', movieData, {
    headers: { Authorization: `Bearer ${token}` },
  });
