import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';

function BookingSummary() {
  const { id } = useParams();
  const [booking, setBooking] = useState(null);

  useEffect(() => {
    const fetchBooking = async () => {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5000/api/bookings/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      const data = await res.json();
      setBooking(data);
    };

    fetchBooking();
  }, [id]);

  if (!booking) return <div className="p-6 text-center">Loading booking...</div>;

  return (
    <div className="p-6 max-w-xl mx-auto bg-white shadow rounded">
      <h2 className="text-2xl font-bold text-green-600 mb-4">ðŸŽ‰ Booking Confirmed</h2>
      <p><strong>Movie:</strong> {booking.movieId}</p>
      <p><strong>Theater:</strong> {booking.theater.name}</p>
      <p><strong>Location:</strong> {booking.theater.location}</p>
      <p><strong>Showtime:</strong> {new Date(booking.showtimeDate).toLocaleString()}</p>
      <p><strong>Seats:</strong> {booking.seats.join(', ')}</p>
      <p><strong>Amount Paid:</strong> â‚¹{booking.amount / 100}</p>
    </div>
  );
}

export default BookingSummary;
