import React, { useEffect, useState } from 'react';

function MyBookings() {
  const [bookings, setBookings] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const user = JSON.parse(localStorage.getItem('user'));
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!user || !token) return;

    fetch(`http://localhost:5000/api/bookings/user/${user._id}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => {
        setBookings(data.bookings || []);
        setLoading(false);
      })
      .catch(err => {
        console.error('‚ùå Error fetching bookings:', err);
        setError('Failed to load bookings');
        setLoading(false);
      });
  }, [user, token]);

  const cancelBooking = async (bookingId) => {
    try {
      const res = await fetch(`http://localhost:5000/api/bookings/${bookingId}/cancel`, {
        method: 'PATCH',
        headers: { Authorization: `Bearer ${token}` }
      });

      const data = await res.json();
      setBookings(prev => prev.map(b => b._id === bookingId ? data.booking : b));
    } catch (err) {
      console.error('‚ùå Error cancelling booking:', err);
      setError('Failed to cancel booking');
    }
  };

  if (loading) return <div className="p-6 text-center">Loading your bookings...</div>;
  if (error) return <div className="p-6 text-red-500 text-center">{error}</div>;

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold mb-4">üéüÔ∏è My Bookings</h2>
      {bookings.length === 0 ? (
        <p>No bookings found.</p>
      ) : (
        <ul className="space-y-4">
          {bookings.map(booking => (
            <li key={booking._id} className="border p-4 rounded shadow">
              <h3 className="text-xl font-semibold">
  {booking.movie?.title || 'Unknown Movie'}
</h3>
              <p><strong>Theater:</strong> {booking.theater.name}</p>
              <p><strong>Showtime:</strong> {new Date(booking.showtime).toLocaleString()}</p>
              <p><strong>Seats:</strong> {booking.seats.join(', ')}</p>
              <p><strong>Status:</strong> {booking.status}</p>
              {booking.status === 'confirmed' && (
                <button
                  onClick={() => cancelBooking(booking._id)}
                  className="mt-2 px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
                >
                  Cancel Booking
                </button>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default MyBookings;
