import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5000/api',
});

// 🎬 Movies
export const fetchMovies = () => API.get('/movies');

// 👤 Auth
export const loginUser = (data) => API.post('/users/login', data);
export const signupUser = (data) => API.post('/users/signup', data); // ✅ Added for Signup.jsx

// 🎟️ Bookings
export const bookSeats = (data, token) =>
  API.post('/bookings', data, {
    headers: { Authorization: `Bearer ${token}` },
  });

// 🛡️ Admin
export const getAllBookings = (token) =>
  API.get('/admin/bookings', {
    headers: { Authorization: `Bearer ${token}` },
  });

export const deleteMovie = (movieId, token) =>
  API.delete(`/admin/movies/${movieId}`, {
    headers: { Authorization: `Bearer ${token}` },
  });
