import React, { useEffect, useState } from 'react';
import { getAllBookings } from '../services/api'; // ✅ Modular API call

function BookingHistory() {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    const user = JSON.parse(localStorage.getItem('user'));

    if (!token || !user) {
      setError('You must be logged in to view bookings.');
      setLoading(false);
      return;
    }

    getAllBookings(token)
      .then(res => {
        // Optional: filter bookings by user ID if needed
        const userBookings = res.data.filter(b => b.user === user._id);
        setBookings(userBookings);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching bookings:', err);
        setError('Failed to load bookings.');
        setLoading(false);
      });
  }, []);

  if (loading) {
    return (
      <div className="p-6 text-center text-lg animate-pulse text-gray-600">
        Loading booking history...
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6 text-center text-red-500">
        {error}
      </div>
    );
  }

  if (bookings.length === 0) {
    return (
      <div className="p-6 text-center text-gray-500">
        No bookings found.
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6">Your Bookings</h1>
      <div className="grid gap-6">
        {bookings.map((booking) => (
          <div key={booking._id} className="flex items-center gap-4 p-4 border rounded shadow-sm hover:shadow-md transition">
            <img src={booking.movie.posterUrl} alt={booking.movie.title} className="w-24 rounded" />
            <div>
              <h2 className="text-xl font-semibold">{booking.movie.title}</h2>
              <p className="text-sm text-gray-600">Showtime: {new Date(booking.showtime).toLocaleString()}</p>
              <p className="text-sm text-gray-600">Seats: {booking.seats.join(', ')}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default BookingHistory;
