import React, { useEffect, useState, useMemo } from 'react'; // âœ… Update import
import { useParams, useNavigate } from 'react-router-dom';
import handleRazorpayPayment from '../components/RazorpayCheckout';

function BookMovie() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [movie, setMovie] = useState(null);
  const [selectedTheaterIndex, setSelectedTheaterIndex] = useState('');
  const [selectedShowtime, setSelectedShowtime] = useState('');
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [seatCount, setSeatCount] = useState(1);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

const rows = useMemo(() => ['A', 'B', 'C', 'D'], []);
const cols = useMemo(() => [1, 2, 3, 4], []);

  const pricePerSeat = 250;
  const totalPrice = selectedSeats.length * pricePerSeat;

const seatMap = useMemo(() => {
  return rows.flatMap(row =>
    cols.map(col => ({
      id: `${row}${col}`,
      status: Math.random() < 0.2 ? 'booked' : 'available'
    }))
  );
}, [rows, cols]);


  useEffect(() => {
    const fetchMovieAndTheaters = async () => {
      if (!id) {
        setError('Invalid movie ID');
        return;
      }

      try {
        const movieRes = await fetch(`http://localhost:5000/api/movies/${id}`);
        const movieData = await movieRes.json();

        const title = movieData.title?.trim().toLowerCase();
        if (!title) {
          setError('Movie title missing');
          return;
        }

        const theaterRes = await fetch('http://localhost:5000/api/theaters');
        const theaterData = await theaterRes.json();

        const theaters = Array.isArray(theaterData)
          ? theaterData
          : theaterData.theaters || [];

        const linkedTheaters = theaters.filter(t =>
          Array.isArray(t.movieTitles) &&
          t.movieTitles.some(mt => mt.trim().toLowerCase() === title)
        );

        movieData.theaters = linkedTheaters;
        setMovie(movieData);
      } catch (err) {
        console.error('âŒ Error fetching movie or theaters:', err);
        setError('Failed to load movie or theater data');
      }
    };

    fetchMovieAndTheaters();
  }, [id]);

  const toggleSeat = (seatId) => {
    const seat = seatMap.find(s => s.id === seatId);
    if (seat?.status === 'booked') return;

    if (selectedSeats.includes(seatId)) {
      setSelectedSeats(selectedSeats.filter(s => s !== seatId));
    } else {
      if (selectedSeats.length < seatCount) {
        setSelectedSeats([...selectedSeats, seatId]);
      } else {
        alert(`You can only select ${seatCount} seat(s).`);
      }
    }
  };

  const handleBooking = async () => {
    setError('');
    setSuccess('');

    const token = localStorage.getItem('token');
    const user = JSON.parse(localStorage.getItem('user'));

    if (!token || !user) {
      navigate('/login');
      return null;
    }

    if (!movie || selectedTheaterIndex === '') {
      setError('Movie or theater not selected');
      return null;
    }

    if (!selectedShowtime || selectedSeats.length !== seatCount) {
      setError('Please select showtime and exact number of seats');
      return null;
    }

    const selectedTheater = movie.theaters[parseInt(selectedTheaterIndex)];

    const payload = {
      movieId: id,
      userId: user._id,
      showtimeDate: selectedShowtime,
      seats: selectedSeats,
      theater: {
        name: selectedTheater?.name || '',
        location: selectedTheater?.location || '',
      },
      amount: totalPrice * 100 // Razorpay expects amount in paise
    };

    return payload;
  };

  const initiatePayment = async () => {
    const payload = await handleBooking();
    if (!payload) return;

    try {
      await handleRazorpayPayment(payload);
      setSuccess('ðŸŽ‰ Booking successful!');
      setSelectedSeats([]);
      setSelectedShowtime('');
      setSelectedTheaterIndex('');
    } catch (err) {
      console.error('âŒ Payment initiation failed:', err);
      setError('Payment failed. Please try again.');
    }
  };

  const formatShowtime = (time) => {
  if (!time) return 'Not selected';
  const parsed = new Date(time);
  return isNaN(parsed.getTime()) ? 'Invalid Date' : parsed.toLocaleString('en-IN');
};

  const getYouTubeVideoId = (url) => {
    const match = url?.match(/(?:youtube\.com.*[?&]v=|youtu\.be\/)([^"&?/\\s]{11})/);
    return match ? match[1] : null;
  };

  if (!movie) {
    return (
      <div className="p-6 text-center text-lg animate-pulse text-gray-600">
        Loading movie details...
      </div>
    );
  }

  const youtubeId = getYouTubeVideoId(movie.trailerUrl);

  return (
    <div className="p-6 max-w-6xl mx-auto bg-white text-gray-900 rounded-lg shadow-lg">
      <h2 className="text-4xl font-bold mb-6 text-red-600">{movie.title}</h2>
      <div className="flex flex-col md:flex-row gap-6">
        <img src={movie.posterUrl} alt={movie.title} className="w-64 h-auto rounded shadow-md" />
        <div className="flex-1">
          <p className="text-gray-700 mb-2">{movie.description}</p>
          <p><strong>Genres:</strong> {movie.genre || 'N/A'}</p>
          <p><strong>Rating:</strong> {movie.rating || 'N/A'}/10</p>

          <label className="block mt-4 font-semibold">Select Theater</label>
          <select
            value={selectedTheaterIndex}
            onChange={(e) => {
  setSelectedTheaterIndex(Number(e.target.value)); // âœ… Cast to number
  setSelectedShowtime('');
}}

            className="border p-2 rounded w-full"
          >
            <option value="">--Select a theater--</option>
            {movie.theaters?.map((t, idx) => (
              <option key={idx} value={idx}>
                {t.name} ({t.location})
              </option>
            ))}
          </select>

          <label className="block mt-4 font-semibold">Select Showtime</label>
          <select
            value={selectedShowtime}
            onChange={(e) => setSelectedShowtime(e.target.value)}
            className="border p-2 rounded w-full"
          >
            <option value="">--Select a showtime--</option>
            {selectedTheaterIndex !== '' ? (
              movie.theaters[selectedTheaterIndex]?.showtimes?.map((time) => (
                <option key={time} value={time}>{formatShowtime(time)}</option>
              ))
            ) : (
              <option disabled>No showtimes available</option>
            )}
          </select>

          <label className="block mt-4 font-semibold">Number of Seats</label>
          <select
            value={seatCount}
            onChange={(e) => {
              setSeatCount(Number(e.target.value));
              setSelectedSeats([]);
            }}
            className="border p-2 rounded w-full"
          >
            {[1, 2, 3, 4, 5].map(count => (
              <option key={count} value={count}>{count}</option>
            ))}
          </select>

          <label className="block mt-4 font-semibold">Select Seats</label>
          <div className="flex justify-center mb-4">
            <div className="w-3/4 text-center text-sm font-semibold text-gray-500 border-t-4 border-gray-400 rounded-b-full py-2">
              SCREEN
            </div>
          </div>

          <div className="space-y-3 mt-2">
            {rows.map(row => (
              <div key={row} className="flex gap-2 justify-center">
                {cols.map(col => {
                  const seatId = `${row}${col}`;
                  const seat = seatMap.find(s => s.id === seatId);
                  const isSelected = selectedSeats.includes(seatId);
                  const isBooked = seat?.status === 'booked';

                  return (
                    <button
                      key={seatId}
                      disabled={isBooked}
                      onClick={() => toggleSeat(seatId)}
                      className={`px-3 py-2 rounded border font-semibold
                        ${isBooked ? 'bg-gray-500 text-white cursor-not-allowed' :
                          isSelected ? 'bg-green-600 text-white' : 'bg-gray-200 hover:bg-gray-300'}
                        transition duration-200`}
                    >
                      {seatId}
                    </button>
                  );
                })}
              </div>                            
            ))}
          </div>

          {/* ðŸ’³ Booking Summary */}
          <div className="mt-6 p-4 bg-gray-100 rounded">
            <h3 className="text-lg font-semibold mb-2">Booking Summary</h3>
            <p><strong>Movie:</strong> {movie.title}</p>
            <p><strong>Theater:</strong> {movie.theaters[selectedTheaterIndex]?.name}</p>
            <p><strong>Showtime:</strong> {formatShowtime(selectedShowtime)}</p>
<p><strong>Seats:</strong> {selectedSeats.length > 0 ? selectedSeats.join(', ') : 'None selected'}</p>
            <p className="text-indigo-600 font-bold mt-2">Total Price: â‚¹{totalPrice}</p>
          </div>

          {/* ðŸ›’ Payment Button */}
          <button
  onClick={initiatePayment}
  disabled={selectedSeats.length !== seatCount}
  className={`mt-6 px-6 py-3 rounded font-semibold text-lg ${
    selectedSeats.length !== seatCount
      ? 'bg-gray-400 cursor-not-allowed'
      : 'bg-indigo-600 text-white hover:bg-indigo-700'
  }`}
>
  Pay â‚¹{totalPrice} & Book
</button>


          {/* ðŸ”” Feedback Messages */}
          {error && <p className="text-red-500 mt-2">{error}</p>}
          {success && <p className="text-green-600 mt-2">{success}</p>}
        </div>
      </div>

      {/* ðŸŽ¬ Trailer Section */}
      {youtubeId && (
        <div className="mt-10">
          <h3 className="text-xl font-semibold mb-2">Watch Trailer</h3>
          <div className="relative w-full" style={{ paddingBottom: '56.25%', height: 0 }}>
            <iframe
              className="absolute top-0 left-0 w-full h-full rounded-lg shadow-md"
              src={`https://www.youtube.com/embed/${youtubeId}`}
              title="YouTube trailer"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default BookMovie;
