import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

function Navbar() {
  const navigate = useNavigate();
  const { user, setUser, setIsAuthenticated } = useContext(AuthContext);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('role');
    setUser(null);
    setIsAuthenticated(false);
    navigate('/login');
  };

  const role = localStorage.getItem('role');

  return (
    <nav className="bg-black text-white px-6 py-4 flex flex-col md:flex-row justify-between items-center shadow-md">
      <Link to="/" className="text-2xl font-bold text-red-500 mb-2 md:mb-0">
        ShowSnap
      </Link>

      <div className="flex flex-wrap gap-3 items-center">
      
        {user ? (
          <>
            <span className="text-gray-400">Welcome, {user.name || 'User'}</span>

            <Link to="/bookings" className="bg-gray-800 px-4 py-2 rounded hover:bg-gray-700">
              My Bookings
            </Link>

            {role === 'admin' && (
              <Link to="/admin" className="bg-yellow-600 px-4 py-2 rounded hover:bg-yellow-700">
                Admin Dashboard
              </Link>
            )}

            <button
              onClick={handleLogout}
              className="bg-gray-700 px-4 py-2 rounded hover:bg-gray-600"
            >
              Logout
            </button>
          </>
        ) : (
          <>
            <Link to="/login" className="bg-red-600 px-4 py-2 rounded hover:bg-red-700">
              Login
            </Link>
            <Link to="/signup" className="bg-blue-600 px-4 py-2 rounded hover:bg-blue-700">
              Sign Up
            </Link>
          </>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
