import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Home({ user }) {
  const [movies, setMovies] = useState([]);
  const [featuredIndex, setFeaturedIndex] = useState(0);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedGenre, setSelectedGenre] = useState('');
  const [minRating, setMinRating] = useState(0);
  const navigate = useNavigate();

  // Fetch movies from backend based on filters
  useEffect(() => {
    setLoading(true);
    axios.get("http://localhost:5000/api/movies", {
        params: { genre: selectedGenre, minRating }
      })
      .then(res => {
        setMovies(res.data || []);
        setFeaturedIndex(0); // reset featured index
        setLoading(false);
      })
      .catch(err => {
        console.error("Error fetching movies:", err);
        setMovies([]);
        setLoading(false);
      });
  }, [selectedGenre, minRating]);

  // Rotate featured movie every 5 seconds
  useEffect(() => {
    if (movies.length > 0) {
      const interval = setInterval(() => {
        setFeaturedIndex(prev => (prev + 1) % movies.length);
      }, 5000);
      return () => clearInterval(interval);
    }
  }, [movies]);

  // Filter movies by search term
  const visibleMovies = movies.filter(movie =>
    movie?.title?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Featured movie fallback
  const featuredMovie = movies[featuredIndex] || {};

  // Loading state
  if (loading) {
    return (
      <div className="p-6 text-center text-lg animate-pulse text-gray-600">
        Loading movies...
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto p-4">
      {/* Header */}
      <header className="mb-6 flex justify-between items-center">
        <p className="text-gray-700">
          Welcome, <strong>{user?.name || user?.email || 'Guest'}</strong>
        </p>
      </header>

      {/* Banner */}
      <section className="relative h-96 rounded-xl overflow-hidden shadow-lg mb-8">
        {featuredMovie.posterUrl ? (
          <img
            src={featuredMovie.posterUrl}
            alt={featuredMovie.title || 'Featured Movie'}
            className="w-full h-full object-cover brightness-75"
          />
        ) : (
          <div className="w-full h-full bg-gray-800 flex items-center justify-center text-white text-xl">
            No Featured Poster
          </div>
        )}
        <div className="absolute bottom-0 left-0 p-6 bg-gradient-to-t from-black/80 to-transparent w-full">
          <h2 className="text-3xl text-white font-bold">{featuredMovie.title || 'Untitled'}</h2>
          <p className="text-gray-300 mt-2 max-w-lg">{featuredMovie.description || 'No description available.'}</p>
          <button
            onClick={() => navigate(`/movies/${featuredMovie._id}`)}
            className="mt-4 px-5 py-2 bg-red-600 hover:bg-red-700 rounded text-white font-semibold"
          >
            Book Now
          </button>
        </div>
      </section>

      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search movies..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="w-full p-3 mb-6 rounded-lg bg-gray-900 text-white placeholder-gray-400"
      />

      {/* Filters */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        <select
          value={selectedGenre}
          onChange={(e) => setSelectedGenre(e.target.value)}
          className="p-3 rounded-lg bg-gray-900 text-white w-full"
        >
          <option value="">All Genres</option>
          <option value="Action">Action</option>
          <option value="Comedy">Comedy</option>
          <option value="Drama">Drama</option>
          <option value="Science Fiction">Science Fiction</option>
          <option value="Romance">Romance</option>
        </select>

        <div className="flex flex-col">
          <label className="text-black font-medium mb-1">
            Minimum Rating: {minRating}
          </label>
          <input
            type="range"
            min="0"
            max="10"
            step="0.1"
            value={minRating}
            onChange={(e) => setMinRating(parseFloat(e.target.value))}
            className="w-full"
          />
        </div>

        {/* Reset Filters Button */}
        <button
          onClick={() => {
            setSelectedGenre('');
            setMinRating(0);
            setSearchTerm('');
          }}
          className="p-3 bg-gray-700 text-white rounded-lg hover:bg-gray-600"
        >
          Reset Filters
        </button>
      </div>

      {/* Movie Grid */}
      <section>
        <h3 className="text-2xl font-semibold mb-4">Now Showing</h3>
        {visibleMovies.length === 0 ? (
          <p className="text-center text-gray-400">No movies match your filters.</p>
        ) : (
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-5 gap-6">
            {visibleMovies.map(movie => (
              <div
                key={movie._id}
                onClick={() => navigate(`/movies/${movie._id}`)}
                className="cursor-pointer rounded-lg overflow-hidden shadow-md hover:shadow-xl transform hover:scale-105 transition duration-300"
              >
                {movie.posterUrl ? (
                  <img
                    src={movie.posterUrl}
                    alt={movie.title}
                    className="w-full h-60 object-cover"
                  />
                ) : (
                  <div className="w-full h-60 bg-gray-800 flex items-center justify-center text-white text-sm">
                    No Poster
                  </div>
                )}
                <div className="p-2 bg-white">
                  <h4 className="font-semibold text-lg">{movie.title || 'Untitled'}</h4>
                  <p className="text-sm text-gray-600">{movie.genre || 'Unknown Genre'}</p>
                  <p className="text-sm text-yellow-500 font-semibold mt-1">
                    ⭐ {movie.rating || 'N/A'}
                  </p>
                </div>
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
}

export default Home;
