import React, { useEffect, useState } from 'react';
import { getAllBookings } from '../services/api';

function BookingHistory() {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    const user = JSON.parse(localStorage.getItem('user'));

    if (!token || !user) {
      setError('You must be logged in to view bookings.');
      setLoading(false);
      return;
    }

    getAllBookings(token)
      .then(res => {
        const userBookings = res.data.filter(b => b.user?._id === user._id);
        setBookings(userBookings);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching bookings:', err);
        setError('Failed to load bookings.');
        setLoading(false);
      });
  }, []);

  if (loading) {
    return (
      <div className="p-6 text-center text-lg animate-pulse text-gray-600">
        Loading booking history...
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-6 text-center text-red-500">
        {error}
      </div>
    );
  }

  if (bookings.length === 0) {
    return (
      <div className="p-6 text-center text-gray-500">
        No bookings found.
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6">Your Bookings</h1>
      <div className="grid gap-6">
        {bookings.map((booking) => {
          const movie = booking.movie || {};
          const poster = movie.posterUrl || 'https://via.placeholder.com/100x150?text=No+Image';
          const title = movie.title || 'Untitled';
          const showtime = booking.showtime ? new Date(booking.showtime).toLocaleString() : 'N/A';
          const seats = booking.seats?.join(', ') || 'N/A';
          const theater = booking.theater || {};

          return (
            <div key={booking._id} className="flex items-center gap-4 p-4 border rounded shadow-sm hover:shadow-md transition">
              <img src={poster} alt={title} className="w-24 h-auto rounded object-cover" />
              <div>
                <h2 className="text-xl font-semibold">{title}</h2>
                <p className="text-sm text-gray-600">Showtime: {showtime}</p>
                <p className="text-sm text-gray-600">Seats: {seats}</p>
                {theater.name && (
                  <p className="text-sm text-gray-600">
                    Theater: {theater.name} ({theater.location})
                  </p>
                )}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default BookingHistory;
