// razorpayCheckout.js
import { bookSeats } from '../services/api';
import toast from 'react-hot-toast';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
const RAZORPAY_KEY = process.env.REACT_APP_RAZORPAY_KEY || 'rzp_test_RIn3HX6YPDL3Nf';

// Helper for API calls with token
const apiFetch = async (endpoint, options = {}) => {
  const token = localStorage.getItem('token');
  const res = await fetch(`${API_URL}${endpoint}`, {
    headers: {
      'Content-Type': 'application/json',
      Authorization: `Bearer ${token}`,
      ...options.headers,
    },
    ...options,
  });
  return res.json();
};

const handleRazorpayPayment = async (payload, setLoading, setSuccess) => {
  const token = localStorage.getItem('token');
  const user = JSON.parse(localStorage.getItem('user'));

  if (!token || !user) {
    toast.error('User not authenticated. Please log in.');
    return;
  }

  if (setSuccess) setSuccess('Booking successful!');
toast.success('Booking successful!');

  if (
    !payload ||
    !payload.amount ||
    payload.amount <= 0 ||
    !payload.movieId ||
    !payload.userId ||
    !payload.seats?.length ||
    !payload.theater?.name ||
    !payload.theater?.location ||
    !payload.showtimeDate
  ) {
    toast.error('Incomplete booking details. Please select showtime and seats.');
    return;
  }

  const formattedDate = new Date(payload.showtimeDate).toISOString();

  try {
    setLoading?.(true);

    // 1️⃣ Create Razorpay order
    const { orderId, error: orderError } = await apiFetch('/api/payments/create-order', {
      method: 'POST',
      body: JSON.stringify({ amount: payload.amount }),
    });

    if (orderError || !orderId) {
      toast.error('Failed to create payment order.');
      setLoading?.(false);
      return;
    }

    if (!window.Razorpay) {
      toast.error('Razorpay SDK not loaded. Please refresh.');
      setLoading?.(false);
      return;
    }

    // 2️⃣ Razorpay payment options
    const options = {
      key: RAZORPAY_KEY,
      amount: payload.amount,
      currency: 'INR',
      name: 'ShowSnap',
      description: 'Movie Ticket Booking',
      order_id: orderId,
      handler: async (response) => {
        try {
          const { razorpay_order_id, razorpay_payment_id, razorpay_signature } = response;
          if (!razorpay_order_id || !razorpay_payment_id || !razorpay_signature) {
            toast.error('Incomplete payment response.');
            setLoading?.(false);
            return;
          }

          // 3️⃣ Verify payment on server
          const verifyRes = await apiFetch('/api/payments/verify', {
            method: 'POST',
            body: JSON.stringify({
              razorpay_order_id,
              razorpay_payment_id,
              razorpay_signature,
              movieId: payload.movieId,
              userId: payload.userId,
              seats: payload.seats,
              theater: payload.theater,
              showtimeDate: formattedDate,
            }),
          });

          if (!verifyRes.success) {
            toast.error(verifyRes.error || 'Payment verification failed.');
            setLoading?.(false);
            return;
          }

          // 4️⃣ Book seats in DB
          const bookingRes = await bookSeats(payload, token);
          const bookingId = bookingRes?.data?._id;
          if (!bookingId) {
            toast.error('Booking failed. Please try again.');
            setLoading?.(false);
            return;
          }

          // 5️⃣ Send confirmation email
          try {
            await apiFetch('/api/email/booking-confirmation', {
              method: 'POST',
              body: JSON.stringify({ booking: payload, userEmail: user.email }),
            });
          } catch (emailErr) {
            console.warn('Email not sent:', emailErr.message);
          }

          toast.success('Booking successful!');
          window.location.href = `/my-bookings/${bookingId}`;
        } catch (err) {
          console.error('Payment handler error:', err);
          toast.error('Something went wrong during booking.');
        } finally {
          setLoading?.(false);
        }
      },
      prefill: {
        name: user.name || 'Guest',
        email: user.email || 'guest@example.com',
        contact: user.contact || '9999999999',
      },
      theme: { color: '#F37254' },
    };

    new window.Razorpay(options).open();
  } catch (err) {
    console.error('Error initiating Razorpay:', err);
    toast.error('Unable to initiate payment. Please try again.');
    setLoading?.(false);
  }
};

export default handleRazorpayPayment;
