import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function BookMovie() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [movie, setMovie] = useState(null);
  const [selectedTheater, setSelectedTheater] = useState('');
  const [selectedShowtime, setSelectedShowtime] = useState('');
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [bookedSeats, setBookedSeats] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Fetch movie details
  useEffect(() => {
    fetch(`http://localhost:5000/api/movies/${id}`)
      .then(res => res.json())
      .then(data => setMovie(data))
      .catch(() => setError('Failed to load movie details'));
  }, [id]);

  // Fetch booked seats for selected showtime
  useEffect(() => {
    if (selectedShowtime) {
      fetch(`http://localhost:5000/api/bookings/booked-seats?movieId=${id}&showtime=${selectedShowtime}`)
        .then(res => res.json())
        .then(data => setBookedSeats(data.seats || []))
        .catch(() => setBookedSeats([]));
    }
  }, [selectedShowtime, id]);

  const handleSeatSelect = seat => {
    if (bookedSeats.includes(seat)) return;
    setSelectedSeats(prev =>
      prev.includes(seat) ? prev.filter(s => s !== seat) : [...prev, seat]
    );
  };

  const handlePayment = async () => {
    if (!selectedTheater || !selectedShowtime || selectedSeats.length === 0) {
      setError('Please select theater, showtime, and seats');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const res = await fetch('http://localhost:5000/api/payment', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          movieId: id,
          theater: selectedTheater,
          showtime: selectedShowtime,
          seats: selectedSeats,
          user: 'harshal kharat',
          email: 'kharatharshal746@gmail.com'
        })
      });

      const result = await res.json();
      if (result.success) {
        navigate(`/my-bookings/${result.bookingId}`);
      } else {
        throw new Error(result.message || 'Payment failed');
      }
    } catch (err) {
      setError(`Payment initiation failed: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  if (!movie) return <p className="text-white p-6">Loading movie...</p>;

  return (
    <div className="p-6 max-w-5xl mx-auto text-white">
      {/* 🎬 Movie Header */}
      <div className="flex flex-col md:flex-row gap-6 mb-6">
        <img
          src={movie.posterUrl}
          alt={movie.title}
          className="w-full md:w-64 rounded shadow-lg"
        />
        <div>
          <h1 className="text-4xl font-bold mb-2">{movie.title}</h1>
          <p className="mb-2">{movie.description}</p>
          <p className="mb-1">Genres: {movie.genre}</p>
          <p className="mb-1">Rating: {movie.rating}/10</p>
          <p className="mb-1">Duration: {movie.duration}</p>
          <p className="mb-1">Language: {movie.language}</p>
          {movie.trailerUrl && (
            <div className="mt-4">
              <iframe
                width="100%"
                height="250"
                src={movie.trailerUrl}
                title="Trailer"
                frameBorder="0"
                allowFullScreen
              ></iframe>
            </div>
          )}
        </div>
      </div>

      {/* 🎭 Cast */}
      {movie.cast.length > 0 && (
        <div className="mb-6">
          <h2 className="text-2xl font-semibold mb-2">Cast</h2>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            {movie.cast.map((member, i) => (
              <div key={i} className="text-center">
                <img
                  src={member.photoUrl}
                  alt={member.name}
                  className="w-24 h-24 object-cover rounded-full mx-auto mb-2"
                />
                <p className="font-medium">{member.name}</p>
                <p className="text-sm text-gray-400">{member.role}</p>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* 🎟️ Booking Form */}
      <div className="mb-6">
        <label className="block mb-2">Select Theater:</label>
        <select
          value={selectedTheater}
          onChange={e => setSelectedTheater(e.target.value)}
          className="text-black p-2 rounded w-full"
        >
          <option value="">-- Choose --</option>
          {movie.theaters.map(t => (
            <option key={t.name} value={t.name}>
              {t.name} ({t.location})
            </option>
          ))}
        </select>
      </div>

      <div className="mb-6">
        <label className="block mb-2">Select Showtime:</label>
        <select
          value={selectedShowtime}
          onChange={e => setSelectedShowtime(e.target.value)}
          className="text-black p-2 rounded w-full"
        >
          <option value="">-- Choose --</option>
          {movie.theaters
            .find(t => t.name === selectedTheater)
            ?.showtimes.map((time, i) => (
              <option key={i} value={time}>
                {new Date(time).toLocaleString()}
              </option>
            ))}
        </select>
      </div>

      {/* 🛋️ Curved Screen + Seats */}
      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-2">Select Seats:</h2>
        <div className="relative bg-gray-900 p-6 rounded-lg shadow-inner">
          <div className="absolute top-0 left-1/2 transform -translate-x-1/2 text-gray-400 text-sm">
            Curved Screen
          </div>
          <div className="grid grid-cols-6 gap-3 mt-6">
            {['A1','A2','A3','A4','A5','A6','B1','B2','B3','B4','B5','B6'].map(seat => {
              const isBooked = bookedSeats.includes(seat);
              const isSelected = selectedSeats.includes(seat);
              return (
                <button
                  key={seat}
                  onClick={() => handleSeatSelect(seat)}
                  disabled={isBooked}
                  className={`p-2 rounded text-sm font-medium ${
                    isBooked
                      ? 'bg-red-600 cursor-not-allowed'
                      : isSelected
                      ? 'bg-green-500'
                      : 'bg-gray-700 hover:bg-gray-600'
                  }`}
                >
                  {seat}
                </button>
              );
            })}
          </div>
        </div>
      </div>

      {/* 💳 Payment */}
      <div className="mt-6">
        <p>Selected Seats: {selectedSeats.join(', ') || 'None'}</p>
        <p>Total Price: ₹{selectedSeats.length * 250}</p>
        <button
          onClick={handlePayment}
          disabled={loading}
          className="mt-4 bg-blue-600 hover:bg-blue-700 px-4 py-2 rounded"
        >
          {loading ? 'Processing...' : `Pay ₹${selectedSeats.length * 250} & Book Now`}
        </button>
        {error && <p className="mt-4 text-red-500">{error}</p>}
      </div>
    </div>
  );
}

export default BookMovie;
