import React, { useContext } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { AuthContext } from './context/AuthContext';

import Navbar from './components/Navbar';
import Footer from './components/Footer';

import Home from './pages/Home';
import MovieDetails from './pages/MovieDetails';
import Login from './pages/Login';
import Signup from './pages/Signup';
import BookingHistory from './pages/BookingHistory';
import BookMovie from './pages/BookMovie';
import AdminDashboard from './pages/AdminDashboard'; // ✅ Add this if not already imported

function App() {
  const { isAuthenticated, user, role } = useContext(AuthContext);

  return (
    <>
      <Navbar />
      <Routes>
        <Route path="/" element={<Home user={user} />} />
        <Route path="/movie/:id" element={<MovieDetails />} />
        <Route path="/book/:id" element={isAuthenticated ? <BookMovie /> : <Navigate to="/login" />} />
        <Route path="/bookings" element={isAuthenticated ? <BookingHistory /> : <Navigate to="/login" />} />
        <Route path="/admin" element={isAuthenticated && role === 'admin' ? <AdminDashboard /> : <Navigate to="/" />} />
        <Route path="/login" element={!isAuthenticated ? <Login /> : <Navigate to="/" />} />
        <Route path="/signup" element={!isAuthenticated ? <Signup /> : <Navigate to="/" />} />
      </Routes>
      <Footer />
    </>
  );
}

export default App;
