const handleSubmit = async (e) => {
  e.preventDefault();
  if (!user || !user._id) return toast.error('User not found');

  try {
    const formData = new FormData();
    formData.append('name', profile.name.trim());
    formData.append('email', profile.email.trim());
    if (profile.mobile?.trim()) formData.append('mobile', profile.mobile.trim());
    if (profile.password?.trim()) formData.append('password', profile.password.trim());
    if (photoFile) formData.append('photo', photoFile);

    await updateUserProfile(formData, token);

    toast.success('Profile updated successfully');
    setProfile(prev => ({ ...prev, password: '' }));
  } catch (err) {
    console.error('Profile update error:', err.response?.data || err.message);
    toast.error(err.response?.data?.error || 'Failed to update profile');
  }
};
