import React, { useEffect, useState } from 'react';
import { fetchMovies, getAllBookings, deleteMovie } from '../services/api';
import { useNavigate } from 'react-router-dom';

function AdminDashboard() {
  const [movies, setMovies] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    const role = localStorage.getItem('role');

    if (role !== 'admin') {
      navigate('/');
      return;
    }

    Promise.all([
      fetchMovies(),
      getAllBookings(token)
    ])
      .then(([movieRes, bookingRes]) => {
        setMovies(movieRes.data || []);
        setBookings(bookingRes.data || []);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setError('Failed to load admin data');
        setLoading(false);
      });
  }, [navigate]);

  const handleDelete = async (movieId) => {
    const token = localStorage.getItem('token');
    try {
      await deleteMovie(movieId, token);
      setMovies(prev => prev.filter(m => m._id !== movieId));
    } catch (err) {
      console.error(err);
      alert('Failed to delete movie');
    }
  };

  if (loading) return <div className="p-6 text-center text-lg animate-pulse text-gray-600">Loading admin dashboard...</div>;
  if (error) return <div className="p-6 text-center text-red-500">{error}</div>;

  return (
    <div className="max-w-6xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6">Admin Dashboard</h1>

      {/* üé¨ Movie Management */}
      <section className="mb-10">
        <h2 className="text-2xl font-semibold mb-4">Movies</h2>
        <div className="grid gap-4">
          {movies.map(movie => (
            <div key={movie._id} className="flex items-center justify-between p-4 border rounded shadow-sm">
              <div>
                <h3 className="text-lg font-semibold">{movie.title}</h3>
                <p className="text-sm text-gray-600">{movie.genre} ‚Ä¢ {movie.rating}/10</p>
              </div>
              <button
                onClick={() => handleDelete(movie._id)}
                className="px-3 py-1 bg-red-600 text-white rounded hover:bg-red-700"
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      </section>

      {/* üéüÔ∏è Booking Overview */}
      <section>
        <h2 className="text-2xl font-semibold mb-4">Bookings</h2>
        <div className="grid gap-4">
          {bookings.map(booking => (
            <div key={booking._id} className="p-4 border rounded shadow-sm">
              <h3 className="font-semibold">{booking.movie?.title || 'Unknown Movie'}</h3>
              <p className="text-sm text-gray-600">User: {booking.user?.email || 'N/A'}</p>
              <p className="text-sm text-gray-600">Showtime: {new Date(booking.showtime).toLocaleString()}</p>
              <p className="text-sm text-gray-600">Seats: {booking.seats.join(', ')}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default AdminDashboard;
