import React, { useState, useEffect, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchMovies } from '../services/api';

function Home({ user }) {
  const [movies, setMovies] = useState([]);
  const [featuredIndex, setFeaturedIndex] = useState(0);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedGenre, setSelectedGenre] = useState('');
  const [minRating, setMinRating] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    setLoading(true);
    fetchMovies()
      .then(res => {
        const data = Array.isArray(res.data)
          ? res.data
          : Array.isArray(res.data?.movies)
          ? res.data.movies
          : [];
        console.log('üé¨ Movie data:', data); // Debug log
        setMovies(data);
        setFeaturedIndex(0);
        setLoading(false);
      })
      .catch(err => {
        console.error('‚ùå Error fetching movies:', err.message);
        setMovies([]);
        setLoading(false);
      });
  }, []);

  const visibleMovies = useMemo(() => {
    if (!Array.isArray(movies)) return [];
    return movies.filter(movie =>
      movie?.title?.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (selectedGenre ? movie.genre?.toLowerCase().includes(selectedGenre.toLowerCase()) : true) &&
      movie.rating >= minRating
    );
  }, [movies, searchTerm, selectedGenre, minRating]);

  useEffect(() => {
    if (visibleMovies.length > 0) {
      const interval = setInterval(() => {
        setFeaturedIndex(prev => (prev + 1) % visibleMovies.length);
      }, 5000);
      return () => clearInterval(interval);
    }
  }, [visibleMovies]);

  const featuredMovie =
    visibleMovies.length > 0
      ? visibleMovies[featuredIndex % visibleMovies.length]
      : null;

  const genres = useMemo(() => {
    return Array.isArray(movies)
      ? [...new Set(movies.map(m => m.genre).filter(Boolean))].sort()
      : [];
  }, [movies]);

  if (loading) {
    return (
      <div className="p-6 text-center text-lg animate-pulse text-gray-600">
        Loading movies...
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto p-4 text-white">
      {/* Welcome */}
      <header className="mb-6 flex justify-between items-center">
        <p className="text-gray-400">
          Welcome, <strong>{user?.name || user?.email || 'Guest'}</strong>
        </p>
      </header>

      {/* üé¨ Featured Banner */}
      <section className="relative h-96 rounded-xl overflow-hidden shadow-lg mb-8">
        {featuredMovie?.posterUrl ? (
          <img
            src={featuredMovie.posterUrl}
            alt={featuredMovie.title || 'Featured Movie'}
            className="w-full h-full object-cover brightness-75"
          />
        ) : (
          <div className="w-full h-full bg-gray-800 flex items-center justify-center text-white text-xl">
            No Featured Poster
          </div>
        )}
        <div className="absolute bottom-0 left-0 p-6 bg-gradient-to-t from-black/80 to-transparent w-full">
          <h2 className="text-3xl font-bold">{featuredMovie?.title || 'Untitled'}</h2>
          <p className="text-sm text-gray-400 mb-1">
            {featuredMovie?.genre && (
              <span className="bg-red-600 px-2 py-1 rounded text-white text-xs mr-2">
                {featuredMovie.genre}
              </span>
            )}
            {featuredMovie?.releaseDate && (
              <span>{new Date(featuredMovie.releaseDate).toLocaleDateString()}</span>
            )}
          </p>
          <p className="text-gray-300 mt-2 max-w-lg">
            {featuredMovie?.description || 'No description available.'}
          </p>
          {featuredMovie?._id && (
            <button
              onClick={() => navigate(`/book/${featuredMovie._id}`)}
              className="mt-4 px-5 py-2 bg-red-600 hover:bg-red-700 rounded text-white font-semibold"
            >
              Book Now
            </button>
          )}
        </div>
      </section>

      {/* üîç Search & Filters */}
      <input
        type="text"
        placeholder="Search movies..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="w-full p-3 mb-6 rounded-lg bg-gray-900 text-white placeholder-gray-400"
      />

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        <select
          value={selectedGenre}
          onChange={(e) => setSelectedGenre(e.target.value)}
          className="p-3 rounded-lg bg-gray-900 text-white w-full"
        >
          <option value="">All Genres</option>
          {genres.map(g => (
            <option key={g} value={g}>{g}</option>
          ))}
        </select>

        <div className="flex flex-col">
          <label className="text-sm text-gray-400 mb-1">
            Minimum Rating: <span className="text-yellow-400 font-semibold">{minRating}</span>
          </label>
          <input
            type="range"
            min="0"
            max="10"
            step="0.1"
            value={minRating}
            onChange={(e) => setMinRating(parseFloat(e.target.value))}
            className="w-full accent-red-500"
          />
        </div>

        <button
          onClick={() => {
            setSelectedGenre('');
            setMinRating(0);
            setSearchTerm('');
          }}
          className="p-3 bg-gray-700 text-white rounded-lg hover:bg-gray-600"
        >
          Reset Filters
        </button>
      </div>

      {/* üé• Movie Grid */}
      <section>
        <h3 className="text-2xl font-semibold mb-4">Now Showing</h3>
        {visibleMovies.length === 0 ? (
          <p className="text-center text-gray-400">No movies match your filters.</p>
        ) : (
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-5 gap-6">
            {visibleMovies.map(movie => (
              <div
                key={movie._id}
                onClick={() => movie._id && navigate(`/book/${movie._id}`)}
                className="cursor-pointer rounded-lg overflow-hidden shadow-md hover:shadow-xl transform hover:scale-105 transition duration-300"
              >
                {movie.posterUrl ? (
                  <img
                    src={movie.posterUrl}
                    alt={movie.title}
                    className="w-full h-60 object-cover"
                  />
                ) : (
                  <div className="w-full h-60 bg-gray-800 flex items-center justify-center text-white text-sm">
                    No Poster
                  </div>
                )}
                <div className="p-2 bg-white text-black">
                  <h4 className="font-semibold text-lg">{movie.title || 'Untitled'}</h4>
                  <p className="text-sm text-gray-600">{movie.genre || 'Unknown Genre'}</p>
                  <p className="text-sm text-yellow-500 font-semibold mt-1">
                    ‚≠ê {movie.rating || 'N/A'}
                  </p>
                  {Array.isArray(movie.theaters) && movie.theaters.length > 0 ? (
                    <div className="mt-2 space-y-1 text-xs text-gray-500">
                      {movie.theaters.map((theater, index) => (
                        <div key={index}>
                          üè¢ {theater.name || 'Unnamed Theater'}
                          {Array.isArray(theater.showtimes) && theater.showtimes.length > 0 && (
                            <span className="ml-2 text-gray-400">
                              üé¨ {theater.showtimes.join(', ')}
                            </span>
                          )}
                        </div>
                      ))}
                    </div>
                  ) : (
                    <p className="text-xs text-gray-500 mt-1">No theaters listed</p>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
}

export default Home;
