import React, { createContext, useState, useEffect, useMemo } from 'react';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [role, setRole] = useState('user');
  const [token, setToken] = useState(null);

  // ðŸ” Check if JWT token is expired
  const isTokenExpired = (jwtToken) => {
    try {
      const payload = JSON.parse(atob(jwtToken.split('.')[1]));
      return payload.exp * 1000 < Date.now();
    } catch (error) {
      console.error('âŒ Error decoding token:', error);
      return true;
    }
  };

  // ðŸ§¹ Clear auth state
  const clearAuthState = () => {
    setUser(null);
    setIsAuthenticated(false);
    setRole('user');
    setToken(null);
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('role');
  };

  // ðŸ”‘ Login handler
  const login = (userData, jwtToken) => {
    if (!userData || !jwtToken) return;
    localStorage.setItem('token', jwtToken);
    localStorage.setItem('user', JSON.stringify(userData));
    localStorage.setItem('role', (userData.role || 'user').toLowerCase());
    setUser(userData);
    setIsAuthenticated(true);
    setRole((userData.role || 'user').toLowerCase());
    setToken(jwtToken);
  };

  // ðŸ”„ Sync localStorage on mount
  useEffect(() => {
    const storedToken = localStorage.getItem('token');
    const storedUser = localStorage.getItem('user');
    const storedRole = localStorage.getItem('role');

    if (storedToken && storedUser) {
      if (isTokenExpired(storedToken)) {
        console.warn('âš ï¸ Token expired. Clearing auth state.');
        clearAuthState();
        return;
      }

      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);
        setIsAuthenticated(true);
        setRole(storedRole || parsedUser.role || 'user');
        setToken(storedToken);
      } catch (error) {
        console.error('âŒ Error parsing stored user:', { storedUser, storedToken, error });
        clearAuthState();
      }
    }
  }, []);

  // â±ï¸ Auto-logout on token expiry
  useEffect(() => {
    if (!token) return;
    const interval = setInterval(() => {
      if (isTokenExpired(token)) {
        console.warn('âš ï¸ Token expired. Logging out.');
        clearAuthState();
      }
    }, 60 * 1000); // check every minute

    return () => clearInterval(interval);
  }, [token]);

  // ðŸ”§ Memoized context value for performance
  const contextValue = useMemo(() => ({
    user,
    setUser,
    isAuthenticated,
    setIsAuthenticated,
    role,
    setRole,
    token,
    login,
    clearAuthState
  }), [user, isAuthenticated, role, token]);

  return (
    <AuthContext.Provider value={contextValue}>
      {children}
    </AuthContext.Provider>
  );
};
