import React, { useContext } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { AuthContext } from './context/AuthContext';

import Navbar from './components/Navbar';

import Home from './pages/Home';
import MovieDetails from './pages/MovieDetails';
import Login from './pages/Login';
import Signup from './pages/Signup';
import BookingHistory from './pages/BookingHistory';
import BookMovie from './pages/BookMovie';
import AdminDashboard from './pages/admin/AdminDashboard';
import MyBookings from './pages/MyBookings';
import BookingSummary from './pages/BookingSummary';
import TheatersPage from './pages/TheatersPage';

// üîê Protected Route Wrapper
const ProtectedRoute = ({ isAuthenticated, children }) => {
  return isAuthenticated ? children : <Navigate to="/login" />;
};

// üõ°Ô∏è Admin Route Wrapper
const AdminRoute = ({ isAuthenticated, isAdmin, children }) => {
  return isAuthenticated && isAdmin ? children : <Navigate to="/" />;
};

function App() {
  const { isAuthenticated, user } = useContext(AuthContext);
  const isAdmin = user?.role === 'admin';

  return (
    <>
      <Navbar />

      <main className="min-h-screen bg-gradient-to-br from-black via-gray-900 to-gray-800 text-white">
        <Routes>
          {/* üåê Public Routes */}
          <Route path="/" element={<Home user={user} />} />
          <Route path="/movie/:id" element={<MovieDetails />} />

          {/* üéüÔ∏è Protected Routes */}
          <Route
            path="/book/:id"
            element={
              <ProtectedRoute isAuthenticated={isAuthenticated}>
                <BookMovie />
              </ProtectedRoute>
            }
          />
          <Route
            path="/bookings"
            element={
              <ProtectedRoute isAuthenticated={isAuthenticated}>
                <BookingHistory />
              </ProtectedRoute>
            }
          />
          <Route path="/my-bookings" element={<MyBookings />} />

          {/* üõ°Ô∏è Admin Routes */}
          <Route
            path="/admin"
            element={
              <AdminRoute isAuthenticated={isAuthenticated} isAdmin={isAdmin}>
                <AdminDashboard />
              </AdminRoute>
            }
          />

          {/* üîê Auth Routes */}
          <Route
            path="/login"
            element={!isAuthenticated ? <Login /> : <Navigate to="/" />}
          />
          <Route
            path="/signup"
            element={!isAuthenticated ? <Signup /> : <Navigate to="/" />}
          />

          <Route path="/theaters" element={<TheatersPage />} />
          <Route path="/my-bookings/:id" element={<BookingSummary />} />

          {/* üö´ Fallback */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </main>
    </>
  );
}

export default App;
