import React, { useState, useEffect, useMemo, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchPublicMovies } from '../services/api';
import { AuthContext } from '../context/AuthContext';

function Home() {
  const [movies, setMovies] = useState([]);
  const [featuredIndex, setFeaturedIndex] = useState(0);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedGenre, setSelectedGenre] = useState('');
  const [minRating, setMinRating] = useState(0);
  const [error, setError] = useState('');
  const [trailerModal, setTrailerModal] = useState({ open: false, url: '' });
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);

  // Load movies
  useEffect(() => {
    const loadMovies = async () => {
      try {
        setLoading(true);
        const res = await fetchPublicMovies();
        const movieList = Array.isArray(res.data.movies) ? res.data.movies : [];
        setMovies(movieList);
        setFeaturedIndex(0);
        setLoading(false);
      } catch (err) {
        console.error(err);
        setError('Failed to load movies.');
        setLoading(false);
      }
    };
    loadMovies();
  }, []);

  // Memoized today date
  const today = useMemo(() => new Date(), []);

  // Filter for now showing
  const visibleMovies = useMemo(() => {
    return movies.filter(
      (movie) =>
        movie.title?.toLowerCase().includes(searchTerm.toLowerCase()) &&
        (selectedGenre ? movie.genre?.toLowerCase().includes(selectedGenre.toLowerCase()) : true) &&
        movie.rating >= minRating &&
        new Date(movie.releaseDate) <= today
    );
  }, [movies, searchTerm, selectedGenre, minRating, today]);

  // Filter for coming soon
  const upcomingMovies = useMemo(() => {
    return movies.filter((movie) => new Date(movie.releaseDate) > today);
  }, [movies, today]);

  const genres = useMemo(() => [...new Set(movies.map((m) => m.genre).filter(Boolean))].sort(), [movies]);

  // Featured carousel auto-slide
  useEffect(() => {
    if (visibleMovies.length > 0) {
      const interval = setInterval(() => {
        setFeaturedIndex((prev) => (prev + 1) % visibleMovies.length);
      }, 5000);
      return () => clearInterval(interval);
    }
  }, [visibleMovies]);

  const featuredMovie = visibleMovies[featuredIndex] || null;

  if (loading) return <div className="p-6 text-center text-lg text-gray-600">Loading movies...</div>;
  if (error) return <div className="p-6 text-center text-red-500">{error}</div>;

  return (
    <div className="max-w-7xl mx-auto p-4 text-white">
      {/* Navbar */}
      <header className="flex items-center mb-6 gap-6">
        <h1 className="text-3xl font-bold">ShowSnap</h1>
        <button
          className="px-4 py-2 bg-red-600 hover:bg-red-700 rounded font-semibold"
          onClick={() => window.scrollTo(0, 0)}
        >
          Browse Movies
        </button>
      </header>

      {/* Featured Carousel */}
      <section className="relative h-96 rounded-xl overflow-hidden shadow-lg mb-8">
        {featuredMovie?.posterUrl ? (
          <img
            src={featuredMovie.posterUrl}
            alt={featuredMovie.title}
            className="w-full h-full object-cover brightness-75"
          />
        ) : (
          <div className="w-full h-full bg-gray-800 flex items-center justify-center text-white text-xl">
            No Featured Poster
          </div>
        )}
        <div className="absolute bottom-0 left-0 p-6 bg-gradient-to-t from-black/80 to-transparent w-full">
          <h2 className="text-3xl font-bold">{featuredMovie?.title || 'Untitled'}</h2>
          <p className="text-sm text-gray-400 mb-1">
            {featuredMovie?.genre && (
              <span className="bg-red-600 px-2 py-1 rounded text-white text-xs mr-2">{featuredMovie.genre}</span>
            )}
            {featuredMovie?.releaseDate && (
              <span>{new Date(featuredMovie.releaseDate).toLocaleDateString()}</span>
            )}
          </p>
          <p className="text-gray-300 mt-2 max-w-lg">{featuredMovie?.description || 'No description available.'}</p>
          <div className="mt-4 flex gap-3">
            {featuredMovie?.trailerUrl && (
              <button
                onClick={() => setTrailerModal({ open: true, url: featuredMovie.trailerUrl })}
                className="px-5 py-2 bg-gray-700 hover:bg-gray-600 rounded text-white font-semibold"
              >
                Watch Trailer
              </button>
            )}
            <button
              onClick={() => navigate(`/book/${featuredMovie._id}`)}
              className="px-5 py-2 bg-red-600 hover:bg-red-700 rounded text-white font-semibold"
            >
              Book Now
            </button>
          </div>
        </div>
        <button
          onClick={() => setFeaturedIndex((featuredIndex - 1 + visibleMovies.length) % visibleMovies.length)}
          className="absolute left-2 top-1/2 -translate-y-1/2 bg-black/50 p-2 rounded-full hover:bg-black/70"
        >
          â—€
        </button>
        <button
          onClick={() => setFeaturedIndex((featuredIndex + 1) % visibleMovies.length)}
          className="absolute right-2 top-1/2 -translate-y-1/2 bg-black/50 p-2 rounded-full hover:bg-black/70"
        >
          â–¶
        </button>
      </section>

      {/* Filters */}
      <div className="mb-6">
        <input
          type="text"
          placeholder="Search movies..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="w-full p-3 mb-4 rounded-lg bg-gray-900 text-white placeholder-gray-400"
        />
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <select
            value={selectedGenre}
            onChange={(e) => setSelectedGenre(e.target.value)}
            className="p-3 rounded-lg bg-gray-900 text-white w-full"
          >
            <option value="">All Genres</option>
            {genres.map((g) => (
              <option key={g} value={g}>
                {g}
              </option>
            ))}
          </select>
          <div className="flex flex-col">
            <label className="text-sm text-gray-400 mb-1">
              Minimum Rating: <span className="text-yellow-400 font-semibold">{minRating}</span>
            </label>
            <input
              type="range"
              min="0"
              max="10"
              step="0.1"
              value={minRating}
              onChange={(e) => setMinRating(parseFloat(e.target.value))}
              className="w-full accent-red-500"
            />
          </div>
          <button
            onClick={() => {
              setSelectedGenre('');
              setMinRating(0);
              setSearchTerm('');
            }}
            className="p-3 bg-gray-700 text-white rounded-lg hover:bg-gray-600"
          >
            Reset Filters
          </button>
        </div>
      </div>

      {/* Now Showing */}
      <section>
        <h3 className="text-2xl font-semibold mb-4">Now Showing</h3>
        {visibleMovies.length === 0 ? (
          <p className="text-center text-gray-400">No movies match your filters.</p>
        ) : (
          <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-5 gap-6">
            {visibleMovies.map((movie) => (
              <div
                key={movie._id}
                className="cursor-pointer rounded-lg overflow-hidden shadow-md hover:shadow-xl transform hover:scale-105 transition duration-300 bg-white text-black"
                onClick={() => navigate(`/book/${movie._id}`)}
              >
                {movie.posterUrl ? (
                  <img src={movie.posterUrl} alt={movie.title} className="w-full h-60 object-cover" />
                ) : (
                  <div className="w-full h-60 bg-gray-800 flex items-center justify-center text-white text-sm">
                    No Poster
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Coming Soon */}
      <section className="mt-12">
        <h3 className="text-2xl font-semibold mb-4">Coming Soon</h3>
        {upcomingMovies.length === 0 ? (
          <p className="text-center text-gray-400">No upcoming movies listed.</p>
        ) : (
          <div className="flex gap-6 overflow-x-auto pb-4">
            {upcomingMovies.map((movie) => (
              <div
                key={movie._id}
                className="min-w-[180px] rounded-lg overflow-hidden shadow-md bg-white text-black hover:shadow-xl transition duration-300 cursor-pointer"
                onClick={() => navigate(`/book/${movie._id}`)}
              >
                {movie.posterUrl ? (
                  <img src={movie.posterUrl} alt={movie.title} className="w-full h-60 object-cover" />
                ) : (
                  <div className="w-full h-60 bg-gray-800 flex items-center justify-center text-white text-sm">
                    No Poster
                  </div>
                )}
                <div className="p-3">
                  <h4 className="font-semibold text-lg">{movie.title}</h4>
                  <p className="text-sm text-gray-600">
                    ðŸŽ¬ Releases on: {movie.releaseDate ? new Date(movie.releaseDate).toLocaleDateString() : 'Unknown'}
                  </p>
                </div>
              </div>
            ))}
          </div>
        )}
      </section>

      {/* Trailer Modal */}
      {trailerModal.open && (
        <div className="fixed inset-0 bg-black/80 flex items-center justify-center z-50">
          <div className="relative w-full max-w-3xl bg-black rounded-lg overflow-hidden">
            <button
              onClick={() => setTrailerModal({ open: false, url: '' })}
              className="absolute top-2 right-2 text-white text-xl font-bold px-2 py-1 hover:text-red-500"
            >
              âœ•
            </button>
            <iframe
              className="w-full h-[480px]"
              src={trailerModal.url.replace('watch?v=', 'embed/')}
              title="Trailer"
              frameBorder="0"
              allowFullScreen
            ></iframe>
          </div>
        </div>
      )}

      {/* Footer */}
      <footer className="mt-16 bg-gray-900 text-gray-400 text-sm py-8">
        <div className="max-w-7xl mx-auto px-4 grid grid-cols-1 md:grid-cols-3 gap-6">
          <div>
            <h4 className="text-white font-semibold mb-2">ShowSnap</h4>
            <p>Book your tickets for the latest movies.</p>
          </div>
          <div>
            <h4 className="text-white font-semibold mb-2">Help</h4>
            <ul className="space-y-1">
              <li><button onClick={() => navigate('/')}>FAQ</button></li>
              <li><button onClick={() => navigate('/')}>Contact Support</button></li>
              <li><button onClick={() => navigate('/')}>Terms & Conditions</button></li>
            </ul>
          </div>
          <div>
            <h4 className="text-white font-semibold mb-2">Download Our App</h4>
            <div className="flex gap-2">
              <a href="#" className="bg-gray-800 px-3 py-1 rounded hover:bg-gray-700">Google Play</a>
              <a href="#" className="bg-gray-800 px-3 py-1 rounded hover:bg-gray-700">App Store</a>
            </div>
          </div>
        </div>
        <div className="mt-6 text-center text-gray-500">
          Â© 2025 ShowSnap. All rights reserved.
        </div>
      </footer>
    </div>
  );
}

export default Home;
