import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function MovieDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/movies/${id}`)
      .then(res => {
        setMovie(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error("❌ Error fetching movie:", err.message);
        setLoading(false);
      });
  }, [id]);

  if (loading) {
    return (
      <div className="p-6 text-center text-lg animate-pulse text-gray-600">
        Loading movie details...
      </div>
    );
  }

  if (!movie) {
    return (
      <div className="p-6 text-center text-red-600">
        Movie not found.
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto p-6 text-white">
      {/* 🎬 Poster & Title */}
      <div className="flex flex-col md:flex-row gap-6 mb-8">
        {movie.posterUrl ? (
          <img
            src={movie.posterUrl}
            alt={movie.title}
            className="w-full md:w-1/3 rounded-lg shadow-lg object-cover"
          />
        ) : (
          <div className="w-full md:w-1/3 h-64 bg-gray-800 rounded-lg flex items-center justify-center text-gray-400">
            No Poster Available
          </div>
        )}
        <div className="flex-1">
          <h1 className="text-4xl font-bold text-red-500 mb-2">{movie.title}</h1>
          <p className="text-gray-300 mb-4">{movie.description || 'No description available.'}</p>
          <p className="text-sm text-gray-400">Genre: {movie.genre || 'N/A'}</p>
          <p className="text-sm text-yellow-500">⭐ Rating: {movie.rating || 'N/A'}/10</p>
          <p className="text-sm text-gray-400">Duration: {movie.duration || 'N/A'}</p>
          <p className="text-sm text-gray-400">Language: {movie.language || 'N/A'}</p>
          <p className="text-sm text-gray-400">
            Release Date: {movie.releaseDate ? new Date(movie.releaseDate).toLocaleDateString() : 'N/A'}
          </p>

          {/* 🎥 Trailer & Booking */}
          <div className="flex flex-wrap gap-4 mt-4">
            {movie.trailerUrl && (
              <a
                href={movie.trailerUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
              >
                Watch Trailer
              </a>
            )}
            {movie._id && (
              <button
                onClick={() => navigate(`/book/${movie._id}`)}
                className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700"
              >
                Book Now
              </button>
            )}
          </div>
        </div>
      </div>

      {/* 🏢 Theaters & Showtimes */}
      {movie.theaters?.length > 0 && (
        <div className="mb-8">
          <h2 className="text-2xl font-semibold mb-4 text-red-400">Available Theaters</h2>
          <div className="space-y-6">
            {movie.theaters.map((theater, index) => (
              <div key={index} className="bg-gray-800 p-4 rounded-lg shadow">
                <h3 className="text-lg font-semibold text-white">
                  {theater.name} <span className="text-sm text-gray-400">({theater.location})</span>
                </h3>
                <div className="flex flex-wrap gap-3 mt-2">
                  {theater.showtimes.map((time, idx) => (
                    <div key={idx} className="px-3 py-1 bg-gray-700 text-sm rounded text-white">
                      {new Date(time).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* 🎭 Cast */}
      {movie.cast?.length > 0 && (
        <div>
          <h2 className="text-2xl font-semibold mb-4 text-red-400">Cast</h2>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
            {movie.cast.map((actor, index) => (
              <div key={index} className="text-center">
                {actor.photoUrl ? (
                  <img
                    src={actor.photoUrl}
                    alt={actor.name}
                    className="w-24 h-24 object-cover rounded-full mx-auto mb-2"
                  />
                ) : (
                  <div className="w-24 h-24 bg-gray-700 rounded-full mx-auto mb-2 flex items-center justify-center text-sm text-gray-400">
                    No Photo
                  </div>
                )}
                <p className="font-medium">{actor.name}</p>
                <p className="text-sm text-gray-500">{actor.role}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default MovieDetails;
