import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchMovies, bookSeats } from '../services/api';

function BookMovie() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [movie, setMovie] = useState(null);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [showtime, setShowtime] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    setLoading(true);
    fetchMovies()
      .then(res => {
        const found = res.data.find(m => m._id === id);
        setMovie(found || null);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching movie:', err);
        setError('Failed to load movie details.');
        setLoading(false);
      });
  }, [id]);

  const handleSeatSelect = (seat) => {
    setSelectedSeats(prev =>
      prev.includes(seat) ? prev.filter(s => s !== seat) : [...prev, seat]
    );
  };

  const handleBooking = async () => {
    setError('');
    setSuccess('');
    const token = localStorage.getItem('token');

    if (!token) {
      setError('You must be logged in to book.');
      return;
    }

    if (!showtime || selectedSeats.length === 0) {
      setError('Please select a showtime and at least one seat.');
      return;
    }

    const bookingData = {
      movieId: id,
      showtime,
      seats: selectedSeats
    };

    try {
      await bookSeats(bookingData, token);
      setSuccess('ðŸŽ‰ Booking successful!');
      setSelectedSeats([]);
      setShowtime('');
      setTimeout(() => navigate('/bookings'), 2000);
    } catch (err) {
      console.error(err.response?.data || err.message);
      setError(err.response?.data?.message || 'Booking failed. Try again.');
    }
  };

  if (loading) {
    return <div className="p-6 text-center text-lg animate-pulse text-gray-600">Loading movie...</div>;
  }

  if (!movie) {
    return <div className="p-6 text-center text-red-500">Movie not found.</div>;
  }

  return (
    <div className="max-w-4xl mx-auto p-6 text-white">
      <h1 className="text-3xl font-bold mb-4">{movie.title}</h1>
      <p className="text-gray-300 mb-6">{movie.description}</p>

      {/* Showtime Selection */}
      <div className="mb-6">
        <label className="block mb-2 text-sm text-gray-400">Select Showtime</label>
        <select
          value={showtime}
          onChange={(e) => setShowtime(e.target.value)}
          className="w-full p-3 rounded bg-gray-900 text-white"
        >
          <option value="">-- Choose a showtime --</option>
          {movie.showtimes?.map((time, idx) => (
            <option key={idx} value={time}>
              {new Date(time).toLocaleString()}
            </option>
          ))}
        </select>
      </div>

      {/* Seat Selection */}
      <div className="mb-6">
        <label className="block mb-2 text-sm text-gray-400">Select Seats</label>
        <div className="grid grid-cols-5 gap-3">
          {['A1', 'A2', 'A3', 'A4', 'A5', 'B1', 'B2', 'B3', 'B4', 'B5'].map(seat => (
            <button
              key={seat}
              onClick={() => handleSeatSelect(seat)}
              className={`px-4 py-2 rounded ${
                selectedSeats.includes(seat)
                  ? 'bg-red-600 text-white'
                  : 'bg-gray-800 text-gray-300 hover:bg-gray-700'
              }`}
            >
              {seat}
            </button>
          ))}
        </div>
      </div>

      {/* Feedback */}
      {error && <p className="text-red-500 text-sm mb-4">{error}</p>}
      {success && <p className="text-green-500 text-sm mb-4">{success}</p>}

      {/* Book Button */}
      <button
        onClick={handleBooking}
        className="w-full py-3 bg-red-600 hover:bg-red-700 rounded text-white font-semibold"
      >
        Confirm Booking
      </button>
    </div>
  );
}

export default BookMovie;
