import { useParams, useNavigate, useLocation } from 'react-router-dom';
import handleRazorpayPayment from '../components/RazorpayCheckout'; // Ensure this path is correct
import toast from 'react-hot-toast'; // Make sure react-hot-toast is imported

// Assuming you have an API service. If not, consider creating one for cleaner code.
// For now, using direct fetch calls as in your code.

function BookMovie() {
  const { id } = useParams();
  const navigate = useNavigate();
  const location = useLocation();

  // üé¨ Movie & Booking States
  const [movie, setMovie] = useState(null);
  const [selectedTheaterIndex, setSelectedTheaterIndex] = useState('');
  const [selectedShowtime, setSelectedShowtime] = useState('');
  const [seatCount, setSeatCount] = useState(1);
  const [seatMap, setSeatMap] = useState([]);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [error, setError] = useState('');
  // const [success, setSuccess] = useState(''); // Removed, as handleRazorpayPayment now handles success toast & navigation
  const [loading, setLoading] = useState(false);

  const user = JSON.parse(localStorage.getItem('user'));
  const token = localStorage.getItem('token');

  const rows = useMemo(() => ['A', 'B', 'C', 'D'], []);
  const cols = useMemo(() => [1, 2, 3, 4, 5, 6], []);
  const pricePerSeat = 250; // You might want to make this dynamic or based on seat type
  const totalPrice = selectedSeats.length * pricePerSeat;

  // üîπ Format YouTube URL
  const getYouTubeEmbedUrl = (url) => {
    try {
      const videoId = url.includes('youtu.be')
        ? url.split('youtu.be/')[1]
        : url.split('v=')[1].split('&')[0];
      return `https://www.youtube.com/embed/${videoId}`;
    } catch {
      return '';
    }
  };

  // üîπ Format showtime
  const formatShowtime = (time) => {
    if (!time) return 'Not selected';
    const parsed = new Date(time);
    return isNaN(parsed.getTime()) ? 'Invalid Date' : parsed.toLocaleString('en-IN', {
      hour: '2-digit',
      minute: '2-digit',
      hour12: true,
      day: '2-digit',
      month: 'short',
      year: 'numeric'
    });
  };

  // üîπ Fetch Movie
  useEffect(() => {
    const fetchMovie = async () => {
      if (!id) return setError('Invalid movie ID');
      try {
        // ‚ö†Ô∏è IMPORTANT: Your backend /api/movies/:id controller (getMovieById)
        // MUST populate the 'theaters' field for this to work correctly.
        // E.g., const movie = await Movie.findById(id).populate('theaters');
        const res = await fetch(`http://localhost:5000/api/movies/${id}`);
        const data = await res.json();
        setMovie(data);
      } catch (err) {
        console.error('‚ùå Error fetching movie:', err);
        setError('Failed to load movie data');
      }
    };
    fetchMovie();
  }, [id]);

  // üîπ Pre-select theater from URL
  useEffect(() => {
    if (!movie) return;
    const params = new URLSearchParams(location.search);
    const theaterIndex = params.get('theater');
    if (theaterIndex && movie.theaters?.[Number(theaterIndex)]) { // Check if index is valid and theater exists
      setSelectedTheaterIndex(Number(theaterIndex));
    }
  }, [location.search, movie]);

  // üîπ Fetch Seat Map for selected theater & showtime
  useEffect(() => {
    const fetchSeats = async () => {
      if (selectedTheaterIndex === '' || !selectedShowtime || !movie || !token) return;

      try {
        const theater = movie.theaters[selectedTheaterIndex];
        // ‚ùå Missing Backend Endpoint:
        // You need a backend endpoint like `/api/bookings/seats`
        // that takes movieId, theaterId, and showtime and returns already booked seats.
        // Currently, your `bookingRoutes.js` does not have this specific endpoint.
        const res = await fetch(
          `http://localhost:5000/api/bookings/seats?movieId=${id}&theaterId=${theater._id}&showtime=${encodeURIComponent(selectedShowtime)}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        if (!res.ok) {
          throw new Error('Failed to fetch seat availability');
        }

        const bookedSeats = await res.json(); // Array of booked seat IDs

        // Build seat map with types
        const newSeatMap = rows.flatMap((row) =>
          cols.map((col) => {
            const seatId = `${row}${col}`;
            let type = 'Normal';
            if (col <= 2) type = 'VIP'; // Assuming VIP is columns 1,2
            else if (col >= 3 && col <= 4) type = 'Premium'; // Assuming Premium is columns 3,4

            return {
              id: seatId,
              status: bookedSeats.includes(seatId) ? 'booked' : 'available',
              type,
            };
          })
        );

        setSeatMap(newSeatMap);
        setSelectedSeats([]); // Clear selected seats when showtime/theater changes
      } catch (err) {
        console.error('‚ùå Error fetching seat map:', err);
        setError('Failed to load seat availability');
      }
    };

    fetchSeats();
  }, [selectedTheaterIndex, selectedShowtime, movie, id, token, rows, cols]);

  // üîπ Toggle Seat Selection
  const toggleSeat = (seatId) => {
    const seat = seatMap.find((s) => s.id === seatId);
    if (seat?.status === 'booked') return;

    if (selectedSeats.includes(seatId)) {
      setSelectedSeats(selectedSeats.filter((s) => s !== seatId));
    } else {
      if (selectedSeats.length < seatCount) {
        setSelectedSeats([...selectedSeats, seatId]);
      } else {
        toast.error(`You can only select ${seatCount} seat(s).`); // Changed to toast
      }
    }
  };

  // üîπ Prepare Booking Payload
  const handleBooking = async () => {
    if (!token || !user) {
      navigate('/login');
      return null;
    }

    if (!movie || selectedTheaterIndex === '') {
      setError('Please select a theater');
      return null;
    }

    if (!selectedShowtime || selectedSeats.length !== seatCount) {
      setError('Please select showtime and exact number of seats');
      return null;
    }

    const theater = movie.theaters[selectedTheaterIndex];

    return {
      movieId: id,
      userId: user._id,
      showtimeDate: selectedShowtime,
      seats: selectedSeats,
      theater: { // This structure correctly matches the Booking model's embedded theater object
        name: theater.name,
        location: theater.location,
      },
      amount: totalPrice * 100, // Amount in paisa
    };
  };

  // üîπ Initiate Payment
  const initiatePayment = async () => {
    const payload = await handleBooking();
    if (!payload) return;

    try {
      // ‚úÖ Corrected call: pass navigate instead of setSuccess
      await handleRazorpayPayment(payload, setLoading, navigate);
    } catch (err) {
      console.error('‚ùå Payment failed:', err);
      setError('Payment failed. Please try again.');
    }
  };

  if (!movie) {
    return <div className="p-6 text-center text-lg animate-pulse text-gray-600">Loading movie...</div>;
  }

  return (
    <div className="p-6 max-w-5xl mx-auto bg-white text-gray-900 rounded-lg shadow-lg">
      {/* Movie Title & Poster */}
      <h2 className="text-4xl font-bold mb-6 text-center text-red-600">{movie.title}</h2>
      <div className="flex flex-col md:flex-row justify-center mb-6 gap-6">
        <img
          src={movie.posterUrl}
          alt={movie.title}
          className="w-full md:w-64 h-[360px] object-cover rounded-xl shadow-lg"
        />

        <div className="flex-1">
          <p className="text-gray-700 mb-2">{movie.description}</p>
          <p><strong>Genres:</strong> {movie.genre || 'N/A'}</p>
          <p><strong>Rating:</strong> {movie.rating || 'N/A'}/10</p>
          <p><strong>Duration:</strong> {movie.duration || 'N/A'}</p>
        </div>
      </div>

      {/* Trailer */}
      {movie.trailerUrl && (
        <div className="mb-6">
          <h3 className="text-xl font-semibold mb-2 text-center">Watch Trailer</h3>
          <div className="relative w-full" style={{ paddingBottom: '56.25%', height: 0 }}>
            <iframe
              className="absolute top-0 left-0 w-full h-full rounded-lg shadow-md"
              src={getYouTubeEmbedUrl(movie.trailerUrl)}
              title="YouTube trailer"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            />
          </div>
        </div>
      )}

      {/* Booking Controls: Theater & Showtime */}
      <div className="mb-6">
        {/* Theater Carousel */}
        <div className="mb-4 overflow-x-auto flex gap-3 px-2">
          {movie.theaters?.map((t, idx) => {
            const isSelected = selectedTheaterIndex === idx;
            return (
              <button
                key={t._id || idx} // Use t._id if populated, otherwise fallback to idx
                onClick={() => { setSelectedTheaterIndex(idx); setSelectedShowtime(''); }}
                className={`px-4 py-2 rounded-lg whitespace-nowrap border transition ${
                  isSelected
                    ? 'bg-indigo-600 text-white border-indigo-600'
                    : 'bg-white text-gray-700 border-gray-300 hover:bg-indigo-50'
                }`}
              >
                {t.name} ({t.location})
              </button>
            );
          })}
        </div>

        {/* Showtime Horizontal Scroll */}
        {selectedTheaterIndex !== '' && movie.theaters?.[selectedTheaterIndex] && (
          <div className="mb-4 overflow-x-auto flex gap-3 px-2">
            {movie.theaters[selectedTheaterIndex].showtimes?.map((time, idx) => {
              const isSelected = selectedShowtime === time;
              return (
                <button
                  key={idx}
                  onClick={() => setSelectedShowtime(time)}
                  className={`px-4 py-2 rounded-lg whitespace-nowrap border transition ${
                    isSelected
                      ? 'bg-green-600 text-white border-green-600'
                      : 'bg-white text-gray-700 border-gray-300 hover:bg-green-50'
                  }`}
                >
                  {formatShowtime(time)}
                </button>
              );
            })}
          </div>
        )}

        {/* Seat Count Selector */}
        <div className="mb-4 flex items-center gap-2">
          <label className="font-semibold">Seats:</label>
          <select
            value={seatCount}
            onChange={(e) => { setSeatCount(Number(e.target.value)); setSelectedSeats([]); }}
            className="border p-2 rounded bg-white"
          >
            {[1, 2, 3, 4, 5].map((count) => (
              <option key={count} value={count}>{count}</option>
            ))}
          </select>
        </div>
      </div>

      {/* Screen View */}
      <div className="text-center mb-3 font-semibold text-gray-700">SCREEN THIS WAY</div>

      {/* Seat Grid */}
      {seatMap.length > 0 && (
        <div className="mb-6 overflow-x-auto px-2">
          {rows.map((row) => (
            <div key={row} className="flex gap-2 justify-center flex-wrap mb-2">
              <div className="font-semibold mr-2">{row}</div>
              {cols.map((col) => {
                const seatId = `${row}${col}`;
                const seat = seatMap.find((s) => s.id === seatId);
                const isSelected = selectedSeats.includes(seatId);
                const isBooked = seat?.status === 'booked';

                let seatColor = 'bg-gray-200';
                if (seat?.type === 'VIP') seatColor = 'bg-yellow-400';
                else if (seat?.type === 'Premium') seatColor = 'bg-blue-500';
                if (isBooked) seatColor = 'bg-gray-500 cursor-not-allowed';
                if (isSelected) seatColor = 'bg-green-600 text-white';

                return (
                  <button
                    key={seatId}
                    disabled={isBooked}
                    onClick={() => toggleSeat(seatId)}
                    title={`${seat?.type} - ‚Çπ${seat?.type === 'VIP' ? 400 : seat?.type === 'Premium' ? 300 : 250}`}
                    className={`px-3 py-2 rounded border text-sm font-semibold transition duration-200 ${seatColor} hover:scale-105`}
                  >
                    {seatId}
                  </button>
                );
              })}
            </div>
          ))}
        </div>
      )}

      {/* Legend */}
      <div className="flex justify-center gap-4 mb-6 text-sm flex-wrap">
        <div className="flex items-center gap-1"><div className="w-4 h-4 bg-yellow-400 border"></div> VIP</div>
        <div className="flex items-center gap-1"><div className="w-4 h-4 bg-blue-500 border"></div> Premium</div>
        <div className="flex items-center gap-1"><div className="w-4 h-4 bg-gray-200 border"></div> Normal</div>
        <div className="flex items-center gap-1"><div className="w-4 h-4 bg-gray-500 border"></div> Booked</div>
        <div className="flex items-center gap-1"><div className="w-4 h-4 bg-green-600 border"></div> Selected</div>
      </div>

      {/* Booking Summary */}
      <div className="mt-6 p-4 bg-gray-100 rounded shadow-sm text-center">
        <h3 className="text-lg font-semibold mb-2">Booking Summary</h3>
        <p><strong>Movie:</strong> {movie.title}</p>
        <p><strong>Theater:</strong> {movie.theaters[selectedTheaterIndex]?.name || 'Not selected'}</p>
        <p><strong>Showtime:</strong> {formatShowtime(selectedShowtime)}</p>
        <p><strong>Seats:</strong> {selectedSeats.length > 0 ? selectedSeats.join(', ') : 'None selected'}</p>
        <p><strong>User:</strong> {user?.name} ({user?.email})</p>
        <p className="text-indigo-600 font-bold mt-2">Total Price: ‚Çπ{totalPrice}</p>
      </div>

      {/* Payment / Book Now Button */}
      <button
        onClick={initiatePayment}
        disabled={selectedSeats.length !== seatCount || loading}
        className={`mt-6 px-6 py-3 rounded font-semibold text-lg w-full transition-colors ${
          selectedSeats.length !== seatCount || loading
            ? 'bg-gray-400 cursor-not-allowed'
            : 'bg-indigo-600 text-white hover:bg-indigo-700'
        }`}
      >
        {loading ? 'Processing...' : `Pay ‚Çπ${totalPrice} & Book Now`}
      </button>

      {/* Error Messages */}
      {error && <p className="text-red-500 mt-2 text-center">{error}</p>}
    </div>
  );
}

export default BookMovie;
