import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchMovieById, updateMovie } from '../services/api';

function EditMovie() {
  const { movieId } = useParams();
  const navigate = useNavigate();
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadMovie = async () => {
      const res = await fetchMovieById(movieId);
      setMovie(res.data);
      setLoading(false);
    };
    loadMovie();
  }, [movieId]);

  const handleUpdate = async () => {
    await updateMovie(movieId, movie);
    navigate('/admin');
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Edit Movie</h2>
      <input
        type="text"
        value={movie.title}
        onChange={(e) => setMovie({ ...movie, title: e.target.value })}
        className="w-full p-2 mb-4 border rounded"
      />
      {/* Add other fields like genre, rating, releaseDate, etc. */}
      <button onClick={handleUpdate} className="px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700">
        Save Changes
      </button>
    </div>
  );
}

export default EditMovie;
