import { bookSeats } from '../services/api';

const handleRazorpayPayment = async (payload) => {
  try {
    const token = localStorage.getItem('token');
    const user = JSON.parse(localStorage.getItem('user'));

    if (!token || !user) {
      alert('User not authenticated. Please log in again.');
      return;
    }

    if (
      !payload ||
      !payload.amount ||
      payload.amount <= 0 ||
      !payload.movieId ||
      !payload.userId ||
      !payload.seats?.length ||
      !payload.theater?.name ||
      !payload.theater?.location ||
      !payload.showtimeDate
    ) {
      alert('Incomplete booking details. Please select showtime and seats before proceeding.');
      return;
    }

    const formattedDate = new Date(payload.showtimeDate).toISOString();

    const orderRes = await fetch('http://localhost:5000/api/payments/create-order', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      },
      body: JSON.stringify({ amount: payload.amount })
    });

    const { orderId } = await orderRes.json();

    if (!orderId) {
      alert('Failed to create payment order.');
      return;
    }

    const options = {
      key: 'rzp_test_RIn3HX6YPDL3Nf',
      amount: payload.amount,
      currency: 'INR',
      name: 'ShowSnap',
      description: 'Movie Ticket Booking',
      order_id: orderId,
      handler: async function (response) {
        try {
          if (
            !response.razorpay_order_id ||
            !response.razorpay_payment_id ||
            !response.razorpay_signature
          ) {
            alert('Incomplete payment response.');
            return;
          }

          const verifyRes = await fetch('http://localhost:5000/api/payments/verify', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              Authorization: `Bearer ${token}`
            },
            body: JSON.stringify({
              razorpay_order_id: response.razorpay_order_id,
              razorpay_payment_id: response.razorpay_payment_id,
              razorpay_signature: response.razorpay_signature,
              movieId: payload.movieId,
              userId: payload.userId,
              seats: payload.seats,
              theater: payload.theater,
              showtimeDate: formattedDate
            })
          });

          const result = await verifyRes.json();
          console.log("âœ… Payment verification response:", result);

          if (!verifyRes.ok || !result.success) {
            alert(result.error || 'âŒ Payment verification failed. Booking not completed.');
            return;
          }

          const bookingRes = await bookSeats(payload, token);
          console.log("ðŸ“¦ Booking response:", bookingRes);

          const bookingId = bookingRes?.data?._id;
          if (!bookingId) {
            alert('Booking failed. No booking ID returned.');
            return;
          }

          const emailRes = await fetch('http://localhost:5000/api/email/booking-confirmation', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              Authorization: `Bearer ${token}`
            },
            body: JSON.stringify({
              booking: payload,
              userEmail: user.email
            })
          });

          const emailResult = await emailRes.json();
          console.log("ðŸ“§ Email response:", emailResult);

          window.location.href = `/my-bookings/${bookingId}`;
        } catch (err) {
          console.error('âŒ Error during payment handling:', err);
          alert(err.message || 'Something went wrong during booking.');
        }
      },
      prefill: {
        name: user.name || 'Guest',
        email: user.email || 'guest@example.com',
        contact: user.contact || '9999999999'
      },
      theme: {
        color: '#F37254'
      }
    };

    const rzp = new window.Razorpay(options);
    rzp.open();
  } catch (err) {
    console.error('âŒ Error initiating Razorpay:', err);
    alert(err.message || 'Unable to initiate payment. Please try again.');
  }
};

export default handleRazorpayPayment;
