import React, { useContext, useEffect } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

function Navbar() {
  const navigate = useNavigate();
  const { user, setUser, setIsAuthenticated } = useContext(AuthContext);

  // Sync localStorage with context on refresh
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) {
      setUser(storedUser);
      setIsAuthenticated(true);
    }
  }, [setUser, setIsAuthenticated]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('role');
    setUser(null);
    setIsAuthenticated(false);
    navigate('/login');
  };

  const isAdmin = user?.role === 'admin';
  const firstName = user?.name?.split(' ')[0] || 'User';

  return (
    <nav className="bg-[#121212] text-white px-6 py-4 shadow-md sticky top-0 z-50">
      <div className="max-w-7xl mx-auto flex flex-col md:flex-row justify-between items-center gap-4 flex-wrap">

        {/* üé¨ Logo */}
        <NavLink
          to="/"
          className="text-3xl font-bold text-red-500 hover:text-red-400 transition"
          aria-label="Go to homepage"
        >
          ShowSnap
        </NavLink>

        {/* üß≠ Navigation */}
        <div className="flex flex-wrap gap-6 items-center text-sm">
          <NavLink
            to="/movies"
            className={({ isActive }) =>
              isActive
                ? 'text-red-400 font-semibold'
                : 'hover:text-red-400 transition'
            }
            aria-label="Browse movies"
          >
            Browse Movies
          </NavLink>
        </div>

        {/* üîê Auth & Actions */}
        <div className="flex items-center gap-4">
          {user ? (
            <div className="flex items-center gap-3 flex-wrap">
              <span className="text-gray-400">Hi, {firstName}</span>

              <NavLink
                to="/my-bookings"
                className="bg-gray-800 px-4 py-2 rounded hover:bg-gray-700 transition"
                aria-label="View my bookings"
              >
                My Bookings
              </NavLink>

              {isAdmin && (
                <NavLink
                  to="/admin"
                  className="bg-yellow-600 px-4 py-2 rounded hover:bg-yellow-700 transition"
                  aria-label="Go to admin dashboard"
                >
                  Admin Dashboard
                </NavLink>
              )}

              <button
                onClick={handleLogout}
                className="bg-gray-700 px-4 py-2 rounded hover:bg-gray-600 transition"
                aria-label="Logout"
              >
                Logout
              </button>
            </div>
          ) : (
            <div className="flex gap-3">
              <NavLink
                to="/login"
                className="bg-red-600 px-4 py-2 rounded hover:bg-red-700 transition"
                aria-label="Login"
              >
                Login
              </NavLink>
              <NavLink
                to="/signup"
                className="bg-blue-600 px-4 py-2 rounded hover:bg-blue-700 transition"
                aria-label="Sign up"
              >
                Sign Up
              </NavLink>
            </div>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
