import React, { useEffect, useState } from 'react';
import axios from 'axios';

function BookingHistory() {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  const userId = '64f1abc1234567890abcdef1'; // Replace with actual logged-in user ID

  useEffect(() => {
    axios.get(`http://localhost:5000/api/bookings/${userId}`)
      .then(res => {
        setBookings(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching bookings:', err);
        setLoading(false);
      });
  }, [userId]);

  if (loading) return <div className="p-6 text-center text-lg animate-pulse text-gray-600">Loading booking history...</div>;
  if (bookings.length === 0) return <div className="p-6 text-center text-gray-500">No bookings found.</div>;

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6">Your Bookings</h1>
      <div className="grid gap-6">
        {bookings.map((booking) => (
          <div key={booking._id} className="flex items-center gap-4 p-4 border rounded shadow-sm hover:shadow-md transition">
            <img src={booking.movie.posterUrl} alt={booking.movie.title} className="w-24 rounded" />
            <div>
              <h2 className="text-xl font-semibold">{booking.movie.title}</h2>
              <p className="text-sm text-gray-600">Showtime: {new Date(booking.time).toLocaleString()}</p>
              <p className="text-sm text-gray-600">Seats: {booking.seats.join(', ')}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default BookingHistory;
