import React, { useEffect, useState } from 'react';

function MyBookings() {
  const [bookings, setBookings] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [cancellingId, setCancellingId] = useState(null);

  const user = JSON.parse(localStorage.getItem('user'));
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!user || !token) {
      setError('You must be logged in to view your bookings.');
      setLoading(false);
      return;
    }

    fetch(`http://localhost:5000/api/bookings/user/${user._id}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => {
        const rawBookings = Array.isArray(data.bookings) ? data.bookings : [];

        const uniqueBookings = rawBookings.filter(
          (booking, index, self) =>
            index === self.findIndex(b => b._id === booking._id)
        );

        setBookings(uniqueBookings);
        setLoading(false);
      })
      .catch(err => {
        console.error('❌ Error fetching bookings:', err);
        setError('Failed to load bookings.');
        setLoading(false);
      });
  }, [user, token]);

  const cancelBooking = async (bookingId) => {
    const confirmCancel = window.confirm('Are you sure you want to cancel this booking?');
    if (!confirmCancel) return;

    setCancellingId(bookingId);

    try {
      const res = await fetch(`http://localhost:5000/api/bookings/${bookingId}/cancel`, {
        method: 'PATCH',
        headers: { Authorization: `Bearer ${token}` }
      });

      const data = await res.json();
      setBookings(prev =>
        prev.map(b => b._id === bookingId ? data.booking : b)
      );
      alert('✅ Booking cancelled successfully. A confirmation email has been sent.');
    } catch (err) {
      console.error('❌ Error cancelling booking:', err);
      setError('Failed to cancel booking.');
    } finally {
      setCancellingId(null);
    }
  };

  if (loading) return <div className="p-6 text-center text-gray-600">Loading your bookings...</div>;
  if (error) return <div className="p-6 text-center text-red-500">{error}</div>;

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold mb-4 text-red-600">🎟️ My Bookings</h2>
      {bookings.length === 0 ? (
        <p className="text-gray-500">You haven’t booked any tickets yet.</p>
      ) : (
        <ul className="space-y-4">
          {bookings.map(booking => {
            const movieTitle = booking.movie?.title || 'Unknown Movie';
            const theaterName = booking.theater?.name || 'Unknown Theater';
            const theaterLocation = booking.theater?.location || 'N/A';
            const showtimeFormatted = booking.showtimeDate
              ? new Date(booking.showtimeDate).toLocaleString()
              : 'N/A';
            const seats = Array.isArray(booking.seats) ? booking.seats.join(', ') : 'N/A';

            return (
              <li key={booking._id} className="border p-4 rounded shadow bg-white text-black">
                <h3 className="text-xl font-semibold text-red-600">{movieTitle}</h3>
                <p><strong>Theater:</strong> {theaterName}</p>
                <p><strong>Location:</strong> {theaterLocation}</p>
                <p><strong>Showtime:</strong> {showtimeFormatted}</p>
                <p><strong>Seats:</strong> {seats}</p>
                <p><strong>Status:</strong> {booking.status}</p>
                {booking.status === 'confirmed' ? (
                  <button
                    onClick={() => cancelBooking(booking._id)}
                    disabled={cancellingId === booking._id}
                    className={`mt-2 px-4 py-2 rounded font-semibold ${
                      cancellingId === booking._id
                        ? 'bg-gray-400 text-white cursor-not-allowed'
                        : 'bg-red-600 text-white hover:bg-red-700'
                    }`}
                  >
                    {cancellingId === booking._id ? 'Cancelling...' : 'Cancel Booking'}
                  </button>
                ) : (
                  <span className="mt-2 inline-block px-4 py-2 bg-gray-300 text-gray-700 rounded">
                    Booking Cancelled
                  </span>
                )}
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
}

export default MyBookings;
