import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { bookSeats } from '../services/api';

function BookMovie() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [movie, setMovie] = useState(null);
  const [selectedTheaterIndex, setSelectedTheaterIndex] = useState('');
  const [selectedShowtime, setSelectedShowtime] = useState('');
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [seatCount, setSeatCount] = useState(1);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const rows = ['A', 'B', 'C', 'D'];
  const cols = [1, 2, 3, 4];

  useEffect(() => {
    if (!id) {
      setError('Invalid movie ID');
      return;
    }

    fetch(`http://localhost:5000/api/movies/${id}`)
      .then(res => res.json())
      .then(data => setMovie(data))
      .catch(() => setError('Failed to fetch movie details'));
  }, [id]);

  const toggleSeat = (seat) => {
    if (selectedSeats.includes(seat)) {
      setSelectedSeats(selectedSeats.filter(s => s !== seat));
    } else {
      if (selectedSeats.length < seatCount) {
        setSelectedSeats([...selectedSeats, seat]);
      } else {
        alert(`You can only select ${seatCount} seat(s).`);
      }
    }
  };

  const handleBooking = async () => {
    setError('');
    setSuccess('');
    const token = localStorage.getItem('token');
    if (!token) return navigate('/login');

    if (!movie || selectedTheaterIndex === '') {
      setError('Movie or theater not selected');
      return;
    }

    if (!selectedShowtime || selectedSeats.length !== seatCount) {
      setError('Please select showtime and exact number of seats');
      return;
    }

    const selectedTheater = movie.theaters[selectedTheaterIndex];

    try {
      await bookSeats({
        movieId: id,
        showtime: selectedShowtime,
        seats: selectedSeats,
        theater: {
          name: selectedTheater.name,
          location: selectedTheater.location
        }
      }, token);

      setSuccess('Booking successful!');
      setSelectedSeats([]);
      setSelectedShowtime('');
      setSelectedTheaterIndex('');
    } catch (err) {
      console.error('Booking error:', err.response?.data || err.message);
      setError(err.response?.data?.message || 'Booking failed');
    }
  };

  const formatShowtime = (time) => {
    try {
      const parsed = new Date(Date.parse(time));
      return isNaN(parsed.getTime()) ? 'Invalid Date' : parsed.toLocaleString('en-IN');
    } catch {
      return 'Invalid Date';
    }
  };

  const getYouTubeVideoId = (url) => {
    const match = url?.match(/(?:youtube\.com.*[?&]v=|youtu\.be\/)([^"&?/\s]{11})/);
    return match ? match[1] : null;
  };

  if (!movie) {
    return (
      <div className="p-6 text-center text-lg animate-pulse text-gray-600">
        Loading movie details...
      </div>
    );
  }

  const youtubeId = getYouTubeVideoId(movie.trailerUrl);

  return (
    <div className="p-6 max-w-5xl mx-auto bg-gray-900 text-white rounded-lg shadow-lg">
      <h2 className="text-4xl font-bold mb-4 text-red-500">{movie.title}</h2>
      <div className="flex flex-col md:flex-row gap-6">
        <img src={movie.posterUrl} alt={movie.title} className="w-64 h-auto rounded shadow-md" />
        <div className="flex-1">
          <p className="text-gray-300 mb-2">{movie.description}</p>
          <p><strong>Genres:</strong> {movie.genre || 'N/A'}</p>
          <p><strong>Rating:</strong> {movie.rating || 'N/A'}/10</p>

          {/* Theater Selection */}
          <label className="block mt-4 font-semibold">Select Theater</label>
          <select
            value={selectedTheaterIndex}
            onChange={(e) => {
              setSelectedTheaterIndex(e.target.value);
              setSelectedShowtime('');
            }}
            className="border p-2 rounded w-full text-black"
          >
            <option value="">--Select a theater--</option>
            {movie.theaters?.map((t, idx) => (
              <option key={idx} value={idx}>
                {t.name} ({t.location})
              </option>
            ))}
          </select>

          {/* Showtime Selection */}
          <label className="block mt-4 font-semibold">Select Showtime</label>
          <select
            value={selectedShowtime}
            onChange={(e) => setSelectedShowtime(e.target.value)}
            className="border p-2 rounded w-full text-black"
          >
            <option value="">--Select a showtime--</option>
            {selectedTheaterIndex !== '' && movie.theaters[selectedTheaterIndex]?.showtimes?.length > 0 ? (
              movie.theaters[selectedTheaterIndex].showtimes.map((time) => (
                <option key={time} value={time}>{formatShowtime(time)}</option>
              ))
            ) : (
              <option disabled>No showtimes available</option>
            )}
          </select>

          {/* Seat Count */}
          <label className="block mt-4 font-semibold">How many seats?</label>
          <select
            value={seatCount}
            onChange={(e) => {
              setSeatCount(Number(e.target.value));
              setSelectedSeats([]);
            }}
            className="border p-2 rounded w-full text-black"
          >
            {[1, 2, 3, 4, 5].map(count => (
              <option key={count} value={count}>{count}</option>
            ))}
          </select>

          {/* Seat Selection */}
          <label className="block mt-4 font-semibold">Select Seats</label>
          <div className="flex justify-center mb-4">
            <div className="w-3/4 text-center text-sm font-semibold text-gray-400 border-t-4 border-gray-500 rounded-b-full py-2">
              SCREEN
            </div>
          </div>

          <div className="space-y-3 mt-2">
            {rows.map(row => (
              <div key={row} className="flex gap-2 justify-center">
                {cols.map(col => {
                  const seatId = `${row}${col}`;
                  const isSelected = selectedSeats.includes(seatId);

                  return (
                    <button
                      key={seatId}
                      onClick={() => toggleSeat(seatId)}
                      className={`px-3 py-2 rounded border font-semibold
                        ${isSelected ? 'bg-green-500 text-white' : 'bg-gray-700 hover:bg-gray-600'}
                        transition duration-200`}
                    >
                      {seatId}
                    </button>
                  );
                })}
              </div>
            ))}
          </div>

          <button
            onClick={handleBooking}
            className="mt-6 px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
          >
            Book Now
          </button>
          {error && <p className="text-red-400 mt-2">{error}</p>}
          {success && <p className="text-green-400 mt-2">{success}</p>}
        </div>
      </div>

      {youtubeId && (
        <div className="mt-8">
          <h3 className="text-xl font-semibold mb-2">Trailer</h3>
          <div className="aspect-w-16 aspect-h-9">
            <iframe
              className="w-full h-64 md:h-96 rounded-lg shadow-md"
              src={`https://www.youtube.com/embed/${youtubeId}`}
              title="YouTube trailer"
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
            />
          </div>
        </div>
      )}
    </div>
  );
}

export default BookMovie;
