import axios from 'axios';

// ðŸŒ Create Axios instance
const API = axios.create({
  baseURL: process.env.REACT_APP_API_BASE_URL || 'http://localhost:5000/api',
  timeout: 10000, // 10s timeout
});

// ðŸ”‘ Request interceptor: inject token if available
API.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// âš ï¸ Response interceptor: global error logging
API.interceptors.response.use(
  response => response,
  error => {
    // ðŸ’¡ A cleaner approach: let the component handle the specific toast/alert
    console.error('API Error:', error.response?.data || error.message);
    return Promise.reject(error);
  }
);

// ---
// ðŸŽ¬ Movie APIs
// ---
export const fetchMovies = () => API.get('/movies');
export const getMovieById = (id) => API.get(`/movies/${id}`);
export const createMovie = (movieData) => API.post('/admin/movies', movieData);
export const updateMovie = (id, data) => API.put(`/admin/movies/${id}`, data);
export const deleteMovie = (movieId) => API.delete(`/admin/movies/${movieId}`);
// â„¹ï¸ Note: Your backend doesn't have a '/movies/:id/cast' endpoint.
// If you need this, you should create it in the backend's movieController.js.

// ---
// ðŸ‘¤ User Auth APIs
// ---
export const loginUser = (data) => API.post('/users/login', data);
export const signupUser = (userData) => API.post('/users/register', userData);

// ---
// ðŸŽŸï¸ Booking APIs
// ---
// âŒ Redundancy removed: The payment verification endpoint now handles booking creation.
// export const bookSeats = (data) => API.post('/bookings', data);

// âœ… Correct function for a logged-in user to get their own bookings securely
// This function calls the endpoint that gets user ID from the JWT, not the URL.
export const getAllUserBookings = () => API.get('/bookings');

// âŒ Insecure call removed: Your previous code called an endpoint that
// could expose other users' booking history. This is a critical security fix.
// export const getUserBookings = (userId) => API.get(`/bookings/user/${userId}`);

// âœ… Corrected method: The route for cancelling a booking uses PATCH, not PUT.
export const cancelBooking = (bookingId) => API.patch(`/bookings/${bookingId}/cancel`);

// ---
// ðŸ› ï¸ Admin APIs
// ---
export const getAllBookings = () => API.get('/admin/bookings');
export const getAdminStats = () => API.get('/admin/dashboard');
export const getAllUsers = () => API.get('/admin/users');

// ---
// ðŸ¢ Theater APIs
// ---
export const fetchTheaters = () => API.get('/theaters');

export default API;
