import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Home() {
  const [movies, setMovies] = useState([]);
  const [search, setSearch] = useState('');
  const [genre, setGenre] = useState('');
  const [minRating, setMinRating] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:5000/api/movies')
      .then(res => setMovies(res.data))
      .catch(err => console.error('Error fetching movies:', err));
  }, []);

  const filteredMovies = movies.filter(movie => {
    const matchesSearch = movie.title.toLowerCase().includes(search.toLowerCase());
    const matchesGenre = genre ? movie.genre?.toLowerCase().includes(genre.toLowerCase()) : true;
    const matchesRating = movie.rating >= minRating;
    return matchesSearch && matchesGenre && matchesRating;
  });

  const featured = filteredMovies[0];

  return (
    <div className="bg-gray-950 text-white min-h-screen px-6 py-8">
      {/* Welcome */}
      <h1 className="text-2xl font-semibold mb-4 text-gray-300">Welcome, Guest</h1>

      {/* Featured Movie */}
      {featured && (
        <div className="bg-gray-900 rounded-lg shadow-lg p-6 mb-8">
          <h2 className="text-3xl font-bold text-red-500 mb-2">{featured.title}</h2>
          <p className="text-gray-300 mb-4">{featured.description}</p>
          <button
            onClick={() => navigate(`/book/${featured._id}`)}
            className="bg-red-600 px-4 py-2 rounded hover:bg-red-700"
          >
            Book Now
          </button>
        </div>
      )}

      {/* Filters */}
      <div className="flex flex-col md:flex-row gap-4 mb-6">
        <input
          type="text"
          placeholder="Search movies..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="px-4 py-2 rounded bg-gray-800 text-white placeholder-gray-400 w-full md:w-1/3"
        />
        <select
          value={genre}
          onChange={(e) => setGenre(e.target.value)}
          className="px-4 py-2 rounded bg-gray-800 text-white w-full md:w-1/3"
        >
          <option value="">All Genres</option>
          <option value="Action">Action</option>
          <option value="Drama">Drama</option>
          <option value="Comedy">Comedy</option>
          <option value="Sci-Fi">Sci-Fi</option>
        </select>
        <div className="flex items-center gap-2 w-full md:w-1/3">
          <label htmlFor="rating" className="text-sm text-gray-400">Minimum Rating:</label>
          <input
            type="range"
            id="rating"
            min="0"
            max="10"
            step="0.5"
            value={minRating}
            onChange={(e) => setMinRating(parseFloat(e.target.value))}
            className="w-full accent-purple-500"
          />
          <span className="text-sm text-purple-400">{minRating}</span>
        </div>
      </div>

      <button
        onClick={() => {
          setSearch('');
          setGenre('');
          setMinRating(0);
        }}
        className="mb-6 px-4 py-2 bg-gray-700 rounded hover:bg-gray-600"
      >
        Reset Filters
      </button>

      {/* Now Showing */}
      <h2 className="text-xl font-semibold mb-4 text-red-400">Now Showing</h2>
      <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
        {filteredMovies.map(movie => (
          <div key={movie._id} className="bg-gray-900 rounded-lg shadow-md p-4 flex flex-col items-center">
            <img
              src={movie.posterUrl}
              alt={movie.title}
              className="w-full h-64 object-cover rounded mb-4"
            />
            <h3 className="text-lg font-bold text-center mb-2">{movie.title}</h3>
            <button
              onClick={() => navigate(`/book/${movie._id}`)}
              className="bg-red-600 px-3 py-1 rounded hover:bg-red-700"
            >
              Book Now
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
